      }
    },
    [flashTarget, navigate, updateSettings],
  );

  useEffect(() => {
    return subscribeTutorialGoTo(handleTutorialGoTo);
  }, [handleTutorialGoTo]);

  const renderUISelector = () => (
    <UISelector
      settings={settings}
      updateSettings={updateSettings}
      onClose={() => setShowModeSelector(false)}
    />
  );

  const VoiceOrbSurface: React.FC = () => {
    const {
      voiceState,
      isOrbVisible,
      setOrbVisible,
      setActivePersona,
      setActiveModule,
      startListening,
      stopListening,
      audioLevel,
      biometricReading,
    } = useVoiceState();

    // SECURITY: Emergency Privacy Kill Switch (Ctrl+Shift+M)
    useEffect(() => {
      const handleEmergencyPrivacyKillSwitch = (e: KeyboardEvent) => {
        if (e.ctrlKey && e.shiftKey && e.key === 'M') {
          e.preventDefault();
          console.log("[SECURITY] Emergency privacy kill switch activated - disabling microphone");

          // Immediately stop listening and hide orb
          stopListening();
          setOrbVisible(false);

          // Persist setting to prevent auto-restart
          localStorage.setItem('hearthlink_voice_emergency_disabled', 'true');

          // Show confirmation toast
          const toast = document.createElement('div');
          toast.className = 'fixed bottom-20 right-6 z-[10000] bg-red-900/95 text-white px-6 py-3 rounded-lg shadow-2xl border border-red-500/50';
