                <div class="text-xs opacity-80">Microphone disabled. Re-enable in Voice Settings.</div>
              </div>
            </div>
          `;
          document.body.appendChild(toast);
          setTimeout(() => toast.remove(), 5000);
        }
      };

      window.addEventListener('keydown', handleEmergencyPrivacyKillSwitch);
      return () => window.removeEventListener('keydown', handleEmergencyPrivacyKillSwitch);
    }, [stopListening, setOrbVisible]);

    // Auto-configure for launcher: Set Alden as default persona and start listening
    useEffect(() => {
      // Check if emergency kill switch was activated
      const emergencyDisabled = localStorage.getItem('hearthlink_voice_emergency_disabled') === 'true';

      if (settings.uiMode === "hierarchical" && isOrbVisible && !emergencyDisabled) {
        // Set Alden as default persona when on launcher
        setActivePersona("alden");
        setActiveModule(null); // Clear module when on launcher

        // Auto-start listening for wake word when on launcher
        if (voiceState === "idle") {
          startListening();
        }
      }
    }, [settings.uiMode, isOrbVisible, voiceState, setActivePersona, setActiveModule, startListening]);

    return (
      <VoiceOrb
        visible={isOrbVisible}
        state={voiceState}
        onActivate={startListening}
        onDeactivate={stopListening}
        audioLevel={audioLevel}
        biometricReading={biometricReading}
      />
    );
