  { id: "day2-search", mode: "existing", route: "/vault", flash: ['[data-hl-tour="vault-search"]'] },
  { id: "day3-alden-open", mode: "existing", route: "/alden" },
  { id: "day3-task-change", mode: "existing", route: "/alden" },
  { id: "day3-briefing", mode: "existing", route: "/alden" },
  { id: "day6-delegate", mode: "existing", route: "/alden" },
