  { id: "day4-alice-open", mode: "existing", route: "/alice" },
  { id: "day4-focus-coach", mode: "existing", route: "/alice" },
  { id: "day4-escalation", mode: "existing", route: "/alice" },
  { id: "day5-synapse-open", mode: "existing", route: "/synapse" },
  { id: "day5-insight", mode: "existing", route: "/synapse" },
