const useRouteSync = (pathname: string, uiMode: UIMode, updateSettings: (next: Partial<AppSettings>) => void) => {
  useEffect(() => {
    const path = pathname.toLowerCase();
    const isRoot = path === "/" || path === "/launch";
    if (isRoot && uiMode !== "hierarchical") {
      updateSettings({ uiMode: "hierarchical" });
      return;
    }
    if (VALID_MODULE_ROUTES.some((route) => path.includes(route)) && uiMode === "hierarchical") {
      updateSettings({ uiMode: "existing" });
    }
  }, [pathname, uiMode, updateSettings]);
};

const useAppReadySync = (showOnboarding: boolean) => {
  useEffect(() => {
    setAppReadyState(!showOnboarding);
  }, [showOnboarding]);
};

const useModeSelectorShortcut = (setShowModeSelector: React.Dispatch<React.SetStateAction<boolean>>) => {
  useEffect(() => {
    const handleKeyDown = (event: KeyboardEvent) => {
      if (!event.ctrlKey || !event.shiftKey || event.key !== "D") return;
      event.preventDefault();
      setShowModeSelector((prev) => !prev);
    };
    window.addEventListener("keydown", handleKeyDown);
    return () => window.removeEventListener("keydown", handleKeyDown);
  }, [setShowModeSelector]);
};

const useFlashTarget = () =>
  useCallback((selector: string, attempts = 12) => {
    if (typeof document === "undefined") return;
