};

type CheckpointStatus = 'proposed' | 'validated' | 'sealed' | 'superseded';

type CheckpointRecord = {
  checkpointId: string;
  runId: string;
  checkpointType: string;
  phase: string;
  status: CheckpointStatus;
  timestamp: string;
  parentId: string | null;
  gitHash: string;
  policyVerdict: string;
  evidenceRefs: string[];
  actor: string;
  payloadJson: string;
  payloadHash: string;
  entryHash: string;
  prevHash: string;
};

type SkillRoot = {
  root: string;
  sourceType: string;
  sourcePriority: number;
