556:  private getInstalledSkills(): InstalledSkill[] {
557:    const discovered = new Map<string, InstalledSkill>();
558:    const candidates = this.getSkillRoots();
559:    for (const root of candidates) {
560:      if (!fs.existsSync(root.root)) continue;
561:      const markdownFiles = this.collectSkillMarkdownFiles(root.root);
562:      for (const markdown of markdownFiles) {
563:        const parsed = this.parseSkillFile(markdown, root);
564:        if (!parsed) continue;
565:        const existing = discovered.get(parsed.key);
566:        if (!existing || this.isPreferredSkill(parsed, existing)) {
567:          discovered.set(parsed.key, parsed);
568:        }
569:      }
570:    }
571:    return Array.from(discovered.values()).sort((a, b) => {
572:      if (a.sourcePriority !== b.sourcePriority) return a.sourcePriority - b.sourcePriority;
573:      return a.label.localeCompare(b.label);
574:    });
575:  }
576:
577:  private getSkillRoots(): SkillRoot[] {
578:    const bases = new Set<string>([
579:      process.cwd(),
580:      path.resolve(process.cwd(), '..'),
581:      path.resolve(process.cwd(), '../..'),
582:      path.resolve(__dirname, '../../..'),
583:      path.resolve(__dirname, '../../../..'),
584:      path.resolve(__dirname, '../../../../..'),
585:    ]);
586:
587:    const roots: SkillRoot[] = [];
588:    const add = (rootPath: string, sourceType: string, sourcePriority: number, admissionState: string): void => {
589:      const normalized = path.resolve(rootPath);
590:      if (roots.some((item) => item.root === normalized)) return;
591:      roots.push({ root: normalized, sourceType, sourcePriority, admissionState });
592:    };
593:
594:    for (const base of bases) {
595:      add(path.join(base, 'FailSafe', 'VSCode', 'skills'), 'project-canonical', 1, 'admitted');
596:      add(path.join(base, 'VSCode', 'skills'), 'project-canonical', 1, 'admitted');
597:      add(path.join(base, '.agent', 'skills'), 'project-local', 2, 'admitted');
598:      add(path.join(base, '.github', 'skills'), 'project-local', 2, 'admitted');
599:      add(path.join(base, 'docs', 'Planning', 'webpanel', '.codex', 'skills'), 'borrowed-app', 4, 'conditional');
600:    }
601:
602:    return roots;
603:  }
604:
605:  private collectSkillMarkdownFiles(root: string): string[] {
606:    const files: string[] = [];
607:    const stack = [root];
608:    while (stack.length > 0) {
609:      const current = stack.pop() as string;
610:      let entries: fs.Dirent[];
611:      try {
612:        entries = fs.readdirSync(current, { withFileTypes: true });
613:      } catch {
614:        continue;
615:      }
616:      for (const entry of entries) {
617:        const full = path.join(current, entry.name);
618:        if (entry.isDirectory()) {
619:          // Ignore disabled/hidden skill trees (for example, quarantine buckets).
620:          if (entry.name.startsWith('.') || entry.name.startsWith('_')) {
621:            continue;
622:          }
623:          stack.push(full);
624:          continue;
625:        }
626:        if (entry.isFile() && entry.name.toLowerCase() === 'skill.md') {
627:          files.push(full);
628:        }
629:      }
630:    }
631:    return files;
632:  }
