634:  private parseSkillFile(filePath: string, rootMeta: SkillRoot): InstalledSkill | null {
635:    let content = '';
636:    try {
637:      content = fs.readFileSync(filePath, 'utf8');
638:    } catch {
639:      return null;
640:    }
641:    const frontmatterMatch = content.match(/^---\s*([\s\S]*?)\s*---/);
642:    const frontmatter = frontmatterMatch ? frontmatterMatch[1] : '';
643:    const rawName = (this.readFrontmatterValue(frontmatter, 'name') || path.basename(path.dirname(filePath))).trim();
644:    const key = rawName.toLowerCase().replace(/[^a-z0-9-_]+/g, '-').replace(/^-+|-+$/g, '');
645:    if (!key) return null;
646:    const label = rawName;
647:    const desc = (this.readFrontmatterValue(frontmatter, 'description') || 'Installed skill').trim();
648:    const metadataAuthor = this.readFrontmatterValue(frontmatter, 'author')
649:      || this.readFrontmatterValue(frontmatter, 'publisher')
650:      || this.readFrontmatterValue(frontmatter, 'metadata.author')
651:      || 'Unknown';
652:    const versionPin = this.readFrontmatterValue(frontmatter, 'version')
653:      || this.readFrontmatterValue(frontmatter, 'metadata.version')
654:      || 'unversioned';
655:    const trustTier = this.readFrontmatterValue(frontmatter, 'trustTier')
656:      || this.readFrontmatterValue(frontmatter, 'trust_tier')
657:      || 'conditional';
658:    const requiredPermissions = this.readFrontmatterList(frontmatter, 'requiredPermissions')
659:      .concat(this.readFrontmatterList(frontmatter, 'required_permissions'));
660:    const sourceMeta = this.readSourceMetadata(path.dirname(filePath));
661:
662:    const sourceRepo = sourceMeta.sourceRepo
663:      || this.readFrontmatterValue(frontmatter, 'sourceRepo')
664:      || this.readFrontmatterValue(frontmatter, 'source_repo')
665:      || 'unknown';
666:    const sourcePath = sourceMeta.sourcePath
667:      || this.readFrontmatterValue(frontmatter, 'sourcePath')
668:      || this.readFrontmatterValue(frontmatter, 'source_path')
669:      || filePath;
670:    const creator = sourceMeta.creator || metadataAuthor;
671:    const admissionState = sourceMeta.admissionState
672:      || this.readFrontmatterValue(frontmatter, 'admissionState')
673:      || this.readFrontmatterValue(frontmatter, 'admission_state')
674:      || rootMeta.admissionState;
675:    const sourceType = sourceMeta.sourceType
676:      || this.readFrontmatterValue(frontmatter, 'sourceType')
677:      || this.readFrontmatterValue(frontmatter, 'source_type')
678:      || rootMeta.sourceType;
679:    const sourcePriorityRaw = sourceMeta.sourcePriority
680:      || this.readFrontmatterValue(frontmatter, 'sourcePriority')
681:      || this.readFrontmatterValue(frontmatter, 'source_priority');
682:    const sourcePriorityNum = Number.parseInt(sourcePriorityRaw, 10);
683:    const sourcePriority = Number.isFinite(sourcePriorityNum) ? sourcePriorityNum : rootMeta.sourcePriority;
684:
685:    return {
686:      key,
687:      label,
688:      desc,
689:      creator: String(creator || 'Unknown').trim(),
690:      sourceRepo: String(sourceRepo || 'unknown').trim(),
691:      sourcePath: String(sourcePath || filePath).trim(),
692:      versionPin: String(versionPin || 'unversioned').trim(),
693:      trustTier: String(trustTier || 'conditional').trim(),
694:      sourceType: String(sourceType || rootMeta.sourceType).trim(),
695:      sourcePriority,
696:      admissionState: String(admissionState || rootMeta.admissionState).trim(),
697:      requiredPermissions: Array.from(new Set(requiredPermissions.map((item) => item.trim()).filter(Boolean))),
698:    };
699:  }
