701:  private readFrontmatterValue(frontmatter: string, key: string): string {
702:    const escaped = key.replace(/[.*+?^${}()|[\]\\]/g, '\\$&');
703:    const dotted = escaped.replace('\\.', '\\s*\\.\\s*');
704:    const match = frontmatter.match(new RegExp(`^\\s*${dotted}\\s*:\\s*(.+)$`, 'mi'));
705:    if (!match?.[1]) return '';
706:    return String(match[1]).trim().replace(/^['"]|['"]$/g, '');
707:  }
708:
709:  private readFrontmatterList(frontmatter: string, key: string): string[] {
710:    const escaped = key.replace(/[.*+?^${}()|[\]\\]/g, '\\$&');
711:    const headerMatch = frontmatter.match(new RegExp(`^\\s*${escaped}\\s*:\\s*(.+)?$`, 'mi'));
712:    if (!headerMatch) return [];
713:    const trailing = String(headerMatch[1] || '').trim();
714:    if (trailing.startsWith('[') && trailing.endsWith(']')) {
715:      return trailing
716:        .slice(1, -1)
717:        .split(',')
718:        .map((item) => item.trim().replace(/^['"]|['"]$/g, ''))
719:        .filter(Boolean);
720:    }
721:    const start = headerMatch.index ?? -1;
722:    if (start < 0) return [];
723:    const rest = frontmatter.slice(start + headerMatch[0].length).split('\n');
724:    const items: string[] = [];
725:    for (const line of rest) {
726:      if (!/^\s*-/.test(line)) {
727:        if (line.trim().length > 0) break;
728:        continue;
729:      }
730:      items.push(line.replace(/^\s*-\s*/, '').trim().replace(/^['"]|['"]$/g, ''));
731:    }
732:    return items;
733:  }
