834:      plan: ['plan', 'strategy', 'architecture', 'design', 'router', 'flow'],
835:      audit: ['audit', 'review', 'security', 'permission', 'verify', 'compliance'],
836:      implement: ['implement', 'integration', 'wiring', 'state', 'plugin', 'build'],
