class WebPanelClient {
  constructor() {
    this.ws = null;
    this.hub = {
      activePlan: null,
      sentinelStatus: null,
      l3Queue: [],
      recentVerdicts: []
    };
    this.reconnectTimer = null;
    this.reconnectAttempts = 0;

