  elements.statusText.textContent = state.connection === 'connected' ? 'Connected' : state.connection === 'disconnected' ? 'Disconnected' : 'Connecting...';

  const groupedResult = skillsPanel.render(state);
  lastPhase = groupedResult.phase;
