      column || vscode.ViewColumn.One,
      {
        enableScripts: true,
        retainContextWhenHidden: true,
        localResourceRoots: [extensionUri],
      },
    );

    ProjectOverviewPanel.currentPanel = new ProjectOverviewPanel(
      panel,
      sentinel,
      qorelogic,
      riskManager,
      intentService,
      eventBus,
    );
