export class AldenBridge extends AsyncBaseBridge<AldenPersonaProps> {
  private personaMemory: AldenPersonaMemory | null = null;
  private tasks: Task[] = [];
  private tasksLoaded: boolean = false;

  constructor(config?: Partial<BridgeConfig>) {
    super({
      agentId: "alden",
      ...config,
    });
  }

  /**
   * Override getMockResponse to provide Alden-specific mock data for browser mode
   */
  protected getMockResponse<T>(command: AgentCommand, params?: any): T {
    const mock = getAldenMockResponse<T>(command, params, this.userId);
    if (mock !== null) return mock;
    return super.getMockResponse<T>(command, params);
  }

  /**
   * Initialize Alden bridge - sets up persona memory only (no IPC calls)
   */
  protected async onInitialize(): Promise<void> {
    this.personaMemory = {
      persona_id: "alden",
      user_id: this.userId || "default_user",
      schema_version: "1.0.0",
      timestamp: new Date().toISOString(),
      traits: {
        openness: 75,
        conscientiousness: 80,
        extraversion: 65,
        agreeableness: 85,
        emotional_stability: 70,
      },
      motivation_style: "supportive",
      trust_level: 1.0,
      feedback_score: 0,
      learning_agility: 0,
      reflective_capacity: 0,
      habit_consistency: 0,
      engagement: 0,
      correction_events: [],
      session_mood: [],
      relationship_log: [],
      user_tags: [],
      provenance: {
        origin: "system",
        action: "initialization",
      },
      last_modified_by: "system",
      editable_fields: ["traits", "motivation_style", "user_tags"],
      audit_log: [],
    };

    this.tasks = [];
    this.tasksLoaded = false;
  }

  /**
   * Cleanup resources
   */
  protected async onDestroy(): Promise<void> {
    this.personaMemory = null;
    this.tasks = [];
    this.tasksLoaded = false;
  }

  /**
   * Get props for Alden React component
   */
  async getProps(): Promise<AldenPersonaProps> {
    if (!this.personaMemory) {
      console.error(
        "[AldenBridge.getProps] CRITICAL - Persona memory not initialized",
      );
      throw new Error(
        "Alden persona memory not initialized. Backend service may be unavailable.",
      );
    }

    if (!this.tasksLoaded) {
      try {
        await this.loadTasks();
        this.tasksLoaded = true;
      } catch (error) {
        console.error("[AldenBridge] Failed to load tasks:", error);
        throw error; // Propagate error to trigger UI boundary
      }
    }

    return {
      personaId: this.personaMemory.persona_id,
      traits: this.personaMemory.traits,
      motivationStyle: this.personaMemory.motivation_style,
      trustLevel: this.personaMemory.trust_level,
      feedbackScore: this.personaMemory.feedback_score,
      habitConsistency: this.personaMemory.habit_consistency,
      onTraitUpdate: this.updateTraits.bind(this),
      onExport: this.exportPersonaMemory.bind(this),
      onCorrection: this.recordCorrection.bind(this),
      auditLog: this.personaMemory.audit_log,
    };
  }

  // ============================================================================
  // Persona Management
  // ============================================================================

  async updateTraits(
    traits: Partial<AldenTraits>,
  ): Promise<BridgeResponse<AldenPersonaMemory>> {
    try {
      await this.invokeCommand("alden_update_trait", { traits });
      if (this.personaMemory) {
        this.personaMemory.traits = { ...this.personaMemory.traits, ...traits };
      }
      this.notifySubscribers({ type: "traits_updated", data: traits });
      return { success: true, data: this.personaMemory! };
    } catch (error: any) {
      return {
        success: false,
