  /**
   * Initialize Core bridge - sets up initial state only (no IPC calls)
   *
   * IMPORTANT: No Tauri IPC calls should be made here to avoid deadlock.
   * IPC calls are deferred until after component mount via lazy loading in getProps().
   */
  protected async onInitialize(): Promise<void> {
    console.log("[CoreBridge.onInitialize] START - Initializing Core bridge");

    // Initialize with empty state - data will be loaded lazily
    this.currentSession = null;
    this.messages = [];
    this.routingRules = [];
    this.conferences = [];
    this.workflows = [];
    this.dataLoaded = false;

    console.log(
      "[CoreBridge.onInitialize] END - Initialized successfully (data will load lazily)",
    );
  }

  /**
   * Cleanup resources
   */
  protected async onDestroy(): Promise<void> {
    this.currentSession = null;
    this.messages = [];
    this.routingRules = [];
    this.dataLoaded = false;
  }

  /**
   * Get props for Core React component
   *
   * Performs lazy loading of session data on first call after component mount.
   * This ensures Tauri IPC is ready and avoids initialization deadlock.
   */
  async getProps(): Promise<CoreProps> {
    // Lazy load session data on first call (safe to use IPC after mount)
    if (!this.dataLoaded) {
      console.log(
        "[CoreBridge.getProps] Lazy loading session data (first call after mount)",
      );
      try {
        // Load or create current session
        let sessionResponse = await this.getCurrentSession();

        // Auto-create session if none exists
        if (sessionResponse.success && !sessionResponse.data) {
          console.log(
            "[CoreBridge.getProps] No active session found, creating new session...",
          );
          sessionResponse = await this.createSession(this.userId);

          if (!sessionResponse.success) {
            console.error(
              "[CoreBridge.getProps] Failed to auto-create session:",
              sessionResponse.error,
            );
          }
        }

        if (sessionResponse.success && sessionResponse.data) {
          this.currentSession = sessionResponse.data;

          // Load messages for current session
          const messagesResponse = await this.getMessages();
          if (messagesResponse.success && messagesResponse.data) {
            this.messages = messagesResponse.data;
          }
        } else {
          // CRITICAL: Do not fallback to mocks. Report the error.
          console.error(
            "[CoreBridge.getProps] Backend session init failed:",
            sessionResponse.error,
          );
          throw new Error(
            `Backend Session Init Failed: ${sessionResponse.error || "Unknown Error"}`,
          );
        }

        // Load routing rules
        const rulesResponse = await this.getRoutingRules();
        if (rulesResponse.success && rulesResponse.data) {
          this.routingRules = rulesResponse.data;
        }

        // Load conferences for this user
        const conferencesResponse = await this.listConferences(this.userId);
        if (conferencesResponse.success && conferencesResponse.data) {
          this.conferences = conferencesResponse.data;
        }

        this.dataLoaded = true;
        console.log("[CoreBridge.getProps] Session data loaded successfully");
      } catch (error) {
        console.warn(
          "[CoreBridge.getProps] Failed to load session data, continuing with empty state:",
          error,
        );
        // Don't throw - allow degraded mode
      }
    }

    if (!this.currentSession) {
      throw new Error("Session not loaded");
    }

    return {
      currentSession: this.currentSession,
      messages: this.messages,
      routingRules: this.routingRules,
      conferences: this.conferences,
      workflows: this.workflows, // TODO: Implement workflow loading
      onMessageSend: this.sendMessage.bind(this),
      onSessionEnd: this.endSession.bind(this),
      onRuleUpdate: this.updateRule.bind(this),
      onConferenceCreate: async (conference) => {
        const response = await this.createConference({
          userId: this.userId || "default_user",
          title: conference.title,
          purpose: conference.purpose,
          conferenceType: conference.conference_type,
          participants: conference.participants?.map((participant) => ({
            agentId: participant.agent_id,
            role: participant.role,
          })),
        });

        if (!response.success) {
          throw new Error(response.error || "Failed to create conference");
        }
        if (!response.data) {
          throw new Error("Conference creation returned empty response");
        }

        return response.data;
      },
      onWorkflowCreate: this.createWorkflow.bind(this),
    };
  }

  // ============================================================================
  // Session Management
  // ============================================================================

  /**
   * Get current session
   * Invokes: core_get_current_session
   */
  async getCurrentSession(): Promise<BridgeResponse<CoreSession | null>> {
    try {
      // Use invokeCommand to ensure auth_token injection
      const session = await this.invokeCommand<CoreSession | null>(
        "core_get_current_session",
        {
          request: {
            user_id: this.userId,
          },
        },
      );
      this.currentSession = session;
      return { success: true, data: session };
    } catch (error) {
      return this.createErrorResponse<CoreSession | null>(
        error,
        "Failed to fetch current session",
        "SESSION_FETCH_ERROR",
      );
    }
  }

  /**
   * Create a new session
   * Invokes: core_create_session
   */
  async createSession(
    userId: string,
    context?: JsonObject,
  ): Promise<BridgeResponse<CoreSession>> {
    try {
      const request: CreateSessionRequest = {
        userId,
        context: context || {},
      };

      const session = await this.invokeCommand<CoreSession>(
        "core_create_session",
        {
          request: {
            user_id: request.userId,
            context: request.context,
          },
        },
      );

      this.currentSession = session;
      this.messages = [];
      this.notifySubscribers({ type: "session_created", data: session });

      return { success: true, data: session };
    } catch (error) {
      return this.createErrorResponse<CoreSession>(
        error,
        "Failed to create session",
        "SESSION_CREATE_ERROR",
      );
    }
  }

  /**
   * Update session context
   * Invokes: core_update_session_context
   */
  async updateSessionContext(
    context: JsonObject,
  ): Promise<BridgeResponse<CoreSession>> {
    if (!this.currentSession) {
      return {
        success: false,
        error: "No active session",
        code: "NO_SESSION",
      };
    }

    try {
      const request: UpdateSessionContextRequest = {
        sessionId: this.currentSession.session_id,
        context,
      };

      const updated = await this.invokeCommand<CoreSession>(
        "core_update_session_context",
        {
          request: {
            session_id: request.sessionId,
            context: request.context,
          },
        },
      );

      this.currentSession = updated;
      this.notifySubscribers({ type: "session_updated", data: updated });

      return { success: true, data: updated };
    } catch (error) {
      return this.createErrorResponse<CoreSession>(
        error,
        "Failed to update session context",
        "SESSION_UPDATE_ERROR",
      );
    }
  }

  /**
   * Add agent to session
   * Invokes: core_add_agent_to_session
   */
  async addAgentToSession(
    agentId: AgentId,
  ): Promise<BridgeResponse<CoreSession>> {
    if (!this.currentSession) {
      return {
        success: false,
        error: "No active session",
        code: "NO_SESSION",
      };
    }

    try {
      const request: AddAgentToSessionRequest = {
        sessionId: this.currentSession.session_id,
        agentId,
      };

      const updated = await this.invokeCommand<CoreSession>(
        "core_add_agent_to_session",
        {
          request: {
            session_id: request.sessionId,
            agent_id: request.agentId,
          },
        },
      );

      this.currentSession = updated;
      this.notifySubscribers({ type: "agent_added", data: { agentId } });

      return { success: true, data: updated };
    } catch (error) {
      return this.createErrorResponse<CoreSession>(
        error,
        "Failed to add agent to session",
        "AGENT_ADD_ERROR",
      );
    }
  }

  /**
   * End current session (not yet implemented in Rust backend)
   */
  async endSession(): Promise<BridgeResponse<void>> {
    if (!this.currentSession) {
      return {
        success: false,
        error: "No active session",
        code: "NO_SESSION",
      };
    }

    console.warn(
      "[CoreBridge] endSession is not yet implemented in Rust backend",
    );

    // Local cleanup
    this.notifySubscribers({
      type: "session_ended",
      data: { sessionId: this.currentSession.session_id },
    });

    this.currentSession = null;
    this.messages = [];

    return { success: true, data: undefined };
  }

  // ============================================================================
  // Message Management
  // ============================================================================

  /**
   * Send a message through Core
   * Invokes: core_send_message
   */
  async sendMessage(
    message: Omit<CoreMessage, "message_id" | "timestamp">,
  ): Promise<BridgeResponse<CoreMessage>> {
    if (!this.currentSession) {
      return {
        success: false,
        error: "No active session",
        code: "NO_SESSION",
      };
    }

    try {
      const request: SendMessageRequest = {
        sessionId: this.currentSession.session_id,
        fromAgent: message.from_agent,
        toAgent: message.to_agent,
        content: message.content,
        priority: message.priority,
      };

      const sent = await this.invokeCommand<CoreMessage>("core_send_message", {
        request: {
          session_id: request.sessionId,
          from_agent: request.fromAgent,
          to_agent: request.toAgent,
          content: request.content,
          priority: request.priority,
        },
      });

      this.messages.push(sent);
      this.notifySubscribers({ type: "message_sent", data: sent });

      return { success: true, data: sent };
    } catch (error) {
      return this.createErrorResponse<CoreMessage>(
        error,
        "Failed to send message",
        "MESSAGE_SEND_ERROR",
      );
    }
  }

