  /**
   * Get messages for current session
   * Invokes: core_get_messages
   */
  async getMessages(
    query?: CoreMessageQuery,
  ): Promise<BridgeResponse<CoreMessage[]>> {
    if (!this.currentSession) {
      return {
        success: false,
        error: "No active session",
        code: "NO_SESSION",
      };
    }

    try {
      const participants = query?.participants;
      const request: GetMessagesRequest = {
        sessionId: this.currentSession.session_id,
        participantA: participants?.[0],
        participantB: participants?.[1],
        channel: query?.channel,
        limit: query?.limit ?? 100,
        offset: query?.offset ?? 0,
      };

      const messages = await this.invokeCommand<CoreMessage[]>(
        "core_get_messages",
        {
          request: (() => {
            const payload: Record<string, unknown> = {
              session_id: request.sessionId,
            };
            if (request.participantA) payload.participant_a = request.participantA;
            if (request.participantB) payload.participant_b = request.participantB;
            if (request.channel) payload.channel = request.channel;
            if (typeof request.limit === "number") payload.limit = request.limit;
            if (typeof request.offset === "number") payload.offset = request.offset;
            return payload;
          })(),
        },
      );

      this.messages = messages;

      return { success: true, data: messages };
    } catch (error) {
      return this.createErrorResponse<CoreMessage[]>(
        error,
        "Failed to fetch messages",
        "MESSAGE_FETCH_ERROR",
      );
    }
  }

