  sendGroupMessage: (content: string) => Promise<void>;
  sendPrivateMessage: (content: string) => Promise<void>;
  refreshHistory: () => Promise<void>;
}

// Generate a unique message ID
const generateMessageId = (): string =>
  `msg-${Date.now()}-${Math.random().toString(36).slice(2, 9)}`;

// Create a user message
const createUserMessage = (
  content: string,
  channelType: "private" | "group",
): ChatMessage => ({
  id: generateMessageId(),
  content,
  senderId: "user",
  senderName: "You",
  senderType: "user",
  timestamp: new Date().toISOString(),
  channelType,
});

// Filter helper: Check if a message belongs to private chat
// Private chat includes: user messages OR messages from resident persona only
const isPrivateMessage = (
  msg: ChatMessage,
  residentPersonaId: SystemPersonaId,
): boolean => {
  // User messages always belong to private chat
  if (msg.senderId === "user" || msg.senderType === "user") {
    return true;
  }
  // Persona messages only belong if they match resident persona
  return msg.personaId === residentPersonaId;
};

// Filter helper: Check if a message belongs to private chat (dynamic version)
const isPrivateMessageDynamic = (
  msg: ChatMessage,
  targetPersonaId: string,
): boolean => {
  // User messages always belong to private chat
  if (msg.senderId === "user" || msg.senderType === "user") {
    return true;
  }
  // Persona messages only belong if they match target persona
  return msg.personaId === targetPersonaId;
};

// Filter helper: Check if a message belongs to group chat
// Group chat includes: user messages OR messages from joined agents
// Muted agents are suppressed from group display.
const isGroupMessage = (
  msg: ChatMessage,
  joinedAgents: Set<string>,
  mutedAgents?: Set<string>,
): boolean => {
  // User messages always belong to group chat
  if (msg.senderId === "user" || msg.senderType === "user") {
    return true;
  }
  // Persona messages only belong if agent is joined
  const agentId = msg.personaId || msg.senderId;
  const normalized = agentId.toLowerCase();
  if (mutedAgents?.has(normalized)) return false;
  return joinedAgents.has(normalized);
};

