    // Attempt to hydrate auth from AuthClient if missing (fix for race condition)
    if (this.requireAuth && (!this.userId || !this.authToken)) {
      const freshUserId = AuthClient.getUserId();
      const freshToken = AuthClient.getAuthToken();
      if (freshUserId) this.userId = freshUserId;
      if (freshToken) this.authToken = freshToken;

      if (this.userId && this.authToken) {
        console.log(
          `[Bridge:${this.agentId}] Late hydration of credentials successful`,
        );
      }
    }

    // Enforce strict auth requirements before proceeding with initialization
    if (this.requireAuth) {
      if (!this.userId) throw AuthError.missingUserId();
      if (!this.authToken) throw AuthError.missingToken();
    }

    if (this.initialized) {
      return;
    }

    try {
      // Perform agent-specific initialization
      await this.onInitialize();
      this.initialized = true;
      console.log(`[Bridge:${this.agentId}] Initialized successfully`);
    } catch (error) {
      console.error(`[Bridge:${this.agentId}] Initialization failed:`, error);
      throw error;
    }
  }

  /**
   * Get props for the agent component
   */
  abstract getProps(): Promise<TProps>;

  /**
   * Send a message to the agent
   */
  async sendMessage(message: unknown): Promise<BridgeResponse<unknown>> {
    try {
      this.assertAuthContext();

      if (!this.initialized) {
        throw BridgeOperationError.notInitialized(this.agentId);
      }

      const command = `${this.agentId}_send_message`;
      const request: CommandRequest = {
        user_id: this.userId,
        message,
      };

      const response = await this.invokeCommand(command as AgentCommand, {
        request,
      });

      this.notifySubscribers({ type: "message_sent", data: message });
      return { success: true, data: response };
    } catch (error) {
      // Type-safe error handling with proper error codes
      return createFailedResponse(error, "Failed to send message");
    }
  }

  /**
   * Subscribe to bridge events
   */
  subscribe(callback: (data: unknown) => void): () => void {
    this.subscribers.add(callback);
    return () => {
      this.subscribers.delete(callback);
    };
  }

  /**
   * Destroy the bridge and cleanup resources
   */
  async destroy(): Promise<void> {
    try {
      await this.onDestroy();
      this.subscribers.clear();
      this.initialized = false;
      console.log(`[Bridge:${this.agentId}] Destroyed successfully`);
    } catch (error) {
      console.error(`[Bridge:${this.agentId}] Destruction failed:`, error);
      throw error;
    }
  }

