import { useState, useEffect, useCallback, useRef } from "react";
import {
  AldenBridge,
  AliceBridge,
  MimicBridge,
  CoreBridge,
  SynapseBridge,
  ArbiterBridge,
  SystemBridge,
  SettingsBridge,
  BridgeFactory,
} from "../bridges";
import { useAuth } from "../contexts/AuthContext";
import { SettingsProps } from "../bridges/SettingsBridge";
import type {
  AgentBridge,
  AgentId,
  AldenPersonaProps,
  AliceProps,
  MimicProps,
  CoreProps,
  SynapseProps,
  ArbiterProps,
  SystemProps,
} from "../types/agents";

/**
 * Base hook for any agent bridge
 */
export function useBridge<TProps>(
  BridgeClass: new (config?: {
    userId?: string;
    authToken?: string;
    requireAuth?: boolean;
  }) => AgentBridge<TProps>,
  agentId: AgentId,
  config?: { userId?: string; authToken?: string; requireAuth?: boolean },
) {
  const {
    userId: contextUserId,
    authToken: contextToken,
    isAuthenticated,
    isLoading: authLoading,
  } = useAuth();

  const [bridge, setBridge] = useState<AgentBridge<TProps> | null>(null);
  const [props, setProps] = useState<TProps | null>(null);
  const [loading, setLoading] = useState(true);
  const [isWaitingForAuth, setIsWaitingForAuth] = useState(false);
  const [error, setError] = useState<Error | null>(null);
  const [retryCount, setRetryCount] = useState(0);

  useEffect(() => {
    let mounted = true;
    let currentBridge: AgentBridge<TProps> | null = null;
    let authTimer: NodeJS.Timeout | null = null;
    let safetyTimer: NodeJS.Timeout | null = null;

    const initBridge = async () => {
      try {
        const requireAuth = config?.requireAuth !== false;
        const effectiveUserId = config?.userId || contextUserId;
        const effectiveToken = config?.authToken || contextToken;

        // PHASE 1: Authentication Guard
        if (requireAuth && (!effectiveUserId || !effectiveToken)) {
          if (authLoading) {
            // Still performing initial boot - be silent
            return;
          }

          // PHASE 2: Silent Wait (0 - 1.5s)
          authTimer = setTimeout(() => {
            if (mounted) setIsWaitingForAuth(true);
          }, 1500);

          // PHASE 3: Safety Timeout (7.0s)
          safetyTimer = setTimeout(() => {
            if (mounted) {
              setError(
                new Error(
                  "Authentication timeout: System took too long to synchronize credentials. Please try refreshing.",
                ),
              );
              setLoading(false);
              setIsWaitingForAuth(false);
            }
          }, 7000);

          return;
        }

        // We have what we need, or don't need auth
        setIsWaitingForAuth(false);
        setLoading(true);
        setError(null);

        currentBridge = await BridgeFactory.createBridge(
          BridgeClass,
          agentId,
          effectiveUserId || undefined,
          effectiveToken || undefined,
          requireAuth,
        );

        if (!mounted) {
          await BridgeFactory.destroyBridge(agentId);
          return;
        }

        const bridgeProps = await currentBridge.getProps();

        if (!mounted) {
          await BridgeFactory.destroyBridge(agentId);
          return;
        }

        setBridge(currentBridge);
        setProps(bridgeProps);

        // Subscribe to bridge updates to keep props in sync
        const bridgeForSub = currentBridge; // Capture for closure
        const unsubscribe = bridgeForSub.subscribe(async (event: unknown) => {
          if (!mounted) return;
          const eventData = event as { type?: string };
          // Refresh props when settings are updated, saved, or reset
          if (
            eventData.type === "settings_updated" ||
            eventData.type === "settings_saved" ||
            eventData.type === "settings_reset"
          ) {
            try {
              const updatedProps = await bridgeForSub.getProps();
              if (mounted) setProps(updatedProps);
            } catch (err) {
              console.warn("[useBridge] Failed to refresh props after update:", err);
            }
          }
        });

        // Store unsubscribe for cleanup
        (currentBridge as any).__unsubscribe = unsubscribe;
      } catch (err) {
        if (mounted) {
          setError(
            err instanceof Error
              ? err
              : new Error("Bridge initialization failed"),
          );
        }
      } finally {
        if (mounted) {
          setLoading(false);
        }
      }
    };

    initBridge();

    return () => {
      mounted = false;
      if (authTimer) clearTimeout(authTimer);
      if (safetyTimer) clearTimeout(safetyTimer);
      // Unsubscribe before destroying
      if (currentBridge && (currentBridge as any).__unsubscribe) {
        (currentBridge as any).__unsubscribe();
      }
      void BridgeFactory.destroyBridge(agentId);
    };
  }, [
    BridgeClass,
    agentId,
    config?.userId,
    config?.authToken,
    config?.requireAuth,
    contextUserId,
    contextToken,
    isAuthenticated,
    authLoading,
    retryCount, // Allow manual retry
  ]);

  const refresh = useCallback(async () => {
    if (!bridge) {
      setRetryCount((c) => c + 1);
      return;
    }

    try {
      const updatedProps = await bridge.getProps();
      setProps(updatedProps);
    } catch (err) {
      setError(
        err instanceof Error ? err : new Error("Failed to refresh props"),
      );
    }
  }, [bridge]);

  return { bridge, props, loading, error, isWaitingForAuth, refresh };
}

/**
 * Hook for Alden bridge
 */
export function useAldenBridge(userId?: string, authToken?: string) {
  const { bridge, props, loading, error, isWaitingForAuth, refresh } =
    useBridge<AldenPersonaProps>(
      AldenBridge,
      "alden",
      userId || authToken ? { userId, authToken } : undefined,
    );

  return {
    bridge: bridge as AldenBridge | null,
    props,
    loading,
    error,
    isWaitingForAuth,
    refresh,
  };
}

