  protected assertAuthContext(): void {
    // ALWAYS try to refresh credentials from AuthClient to handle race conditions
    // where the bridge was created before auth was fully hydrated, or token rotated.
    const freshUserId = AuthClient.getUserId();
    const freshToken = AuthClient.getAuthToken();

    if (freshUserId && freshUserId !== this.userId) {
      console.log(`[Bridge:${this.agentId}] Refreshing UserId from AuthClient`);
      this.userId = freshUserId;
    }

    if (freshToken && freshToken !== this.authToken) {
      // Only log if we are actually changing from 'something' to 'something else' or 'nothing' to 'something'
      // Avoid logging on every call if stable.
      if (!this.authToken) {
        console.log(
          `[Bridge:${this.agentId}] Late-hydrating AuthToken from AuthClient`,
        );
      }
      this.authToken = freshToken;
    }

    // Enforce auth requirement based on bridge mode
    if (this.requireAuth) {
      // Strict mode: require both userId and authToken
      if (!this.userId) {
        throw AuthError.missingUserId();
      }
      if (!this.authToken) {
        throw AuthError.missingToken();
      }
      // Debug log (can be noisy, maybe comment out in prod)
      // console.log(`[Bridge:${this.agentId}] Auth context validated for user: ${this.userId}`);
    } else {
      // Unauthenticated mode: log if credentials are missing
      if (!this.userId || !this.authToken) {
        console.debug(
          `[Bridge:${this.agentId}:UNAUTHENTICATED_MODE] Making IPC call without full credentials.`,
        );
      }
    }
  }
}

/**
 * Bridge Factory
 * Creates and initializes agent bridges
 */
export class BridgeFactory {
  private static bridges: Map<AgentId, AgentBridge> = new Map();
  private static bridgeRefs: Map<AgentId, number> = new Map();

  static async createBridge<TProps>(
    Bridge: new (config: BridgeConfig) => AgentBridge<TProps>,
    agentId: AgentId,
    userId?: string,
    authToken?: string,
    requireAuth: boolean = true,
  ): Promise<AgentBridge<TProps>> {
    try {
      // Return existing bridge if already created
      if (this.bridges.has(agentId)) {
        const refCount = this.bridgeRefs.get(agentId) ?? 0;
        this.bridgeRefs.set(agentId, refCount + 1);
        return this.bridges.get(agentId) as AgentBridge<TProps>;
      }

      // Only validate auth if requireAuth is true
      if (requireAuth) {
        // HARDENING (Track 5): Explicit auth validation before bridge creation
        const effectiveUserId = userId ?? AuthClient.getUserId();
        const effectiveToken = authToken ?? AuthClient.getAuthToken();

        if (!effectiveUserId || !effectiveToken) {
          throw AuthError.missingCredentials();
        }

        // Create and initialize new bridge with explicit credentials
        const bridge = new Bridge({
          agentId,
          userId: effectiveUserId,
          authToken: effectiveToken,
          requireAuth,
        });
        await bridge.initialize();
        this.bridges.set(agentId, bridge);
        this.bridgeRefs.set(agentId, 1);

        console.log(
          `[BridgeFactory] Created bridge for agent "${agentId}" with authenticated user`,
        );

        return bridge;
      } else {
        // Create unauthenticated bridge (for auth flows like ArbiterBridge)
        const bridge = new Bridge({
          agentId,
          userId: userId ?? undefined,
          authToken: authToken ?? undefined,
          requireAuth: false,
        });
        await bridge.initialize();
        this.bridges.set(agentId, bridge);
        this.bridgeRefs.set(agentId, 1);

        console.log(
          `[BridgeFactory] Created unauthenticated bridge for agent "${agentId}" (for auth operations)`,
        );

        return bridge;
      }
    } catch (error) {
      if (isAuthError(error)) {
        console.error(
          `[BridgeFactory] Auth error creating bridge for "${agentId}":`,
          error.message,
        );
      }
      throw error;
    }
  }

  static async destroyBridge(agentId: AgentId): Promise<void> {
    const refCount = this.bridgeRefs.get(agentId) ?? 0;

    if (refCount > 1) {
      this.bridgeRefs.set(agentId, refCount - 1);
      return;
    }

    this.bridgeRefs.delete(agentId);

    const bridge = this.bridges.get(agentId);
    if (bridge) {
      this.bridges.delete(agentId);
      await bridge.destroy();
    }
  }

  static async destroyAll(): Promise<void> {
    const promises = Array.from(this.bridges.keys()).map((agentId) =>
      this.destroyBridge(agentId),
    );
    await Promise.all(promises);
    this.bridgeRefs.clear();
  }

  static getBridge(agentId: AgentId): AgentBridge | undefined {
    return this.bridges.get(agentId);
  }
}

