/**
 * useSession Hook
 *
 * React hook for managing Core session state and real-time chat functionality.
 * Provides access to session data, messages, and typing indicators.
 *
 * Features:
 * - Auto-connects to current session or creates new one
 * - Real-time message updates via CoreBridge subscriptions
 * - Typing indicator management
 * - Message sending with privacy filtering
 * - Session context management
 *
 * @example
 * ```tsx
 * const {
 *   session,
 *   messages,
 *   sendMessage,
 *   isLoading
 * } = useSession('user123');
 * ```
 */

import { useState, useEffect, useCallback, useRef } from 'react';
import { CoreBridge, GetChatHistoryRequest, SendSessionMessageRequest } from '../bridges/CoreBridge';
import type { CoreSession, CoreMessage, AgentId, JsonValue } from '../types/agents';

export interface UseSessionOptions {
  userId: string;
  autoConnect?: boolean;
  pollInterval?: number; // Polling interval for message updates (ms)
}

export interface UseSessionReturn {
  // Session state
  session: CoreSession | null;
  messages: CoreMessage[];
  isLoading: boolean;
  error: string | null;

  // Typing indicators
  typingAgents: Set<AgentId | 'user'>;

  // Actions
  sendMessage: (content: JsonValue, toAgent?: AgentId | 'user', priority?: 'low' | 'normal' | 'high' | 'urgent') => Promise<void>;
  refreshMessages: () => Promise<void>;
  setTyping: (isTyping: boolean) => void;

  // Session management
  reconnect: () => Promise<void>;
}

/**
 * Custom hook for managing Core session and chat
 */
export function useSession(options: UseSessionOptions): UseSessionReturn {
  const { userId, autoConnect = true, pollInterval = 3000 } = options;

  // State
  const [session, setSession] = useState<CoreSession | null>(null);
  const [messages, setMessages] = useState<CoreMessage[]>([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [typingAgents, setTypingAgents] = useState<Set<AgentId | 'user'>>(new Set());

  // Refs
  const bridgeRef = useRef<CoreBridge | null>(null);
  const pollIntervalRef = useRef<NodeJS.Timeout | null>(null);
  const isTypingRef = useRef(false);

  /**
   * Initialize CoreBridge and load session
   */
  const initializeSession = useCallback(async () => {
    try {
      setIsLoading(true);
      setError(null);

      // Create bridge if not exists
      if (!bridgeRef.current) {
        bridgeRef.current = new CoreBridge({ userId });
        await bridgeRef.current.initialize();

        // Subscribe to bridge events
        bridgeRef.current.subscribe((event: any) => {
          if (event.type === 'message_sent') {
            // Add new message to state
            setMessages((prev) => [...prev, event.data as CoreMessage]);
          } else if (event.type === 'session_updated') {
            setSession(event.data as CoreSession);
          }
        });
      }

      // Get or create current session
      const sessionResponse = await bridgeRef.current.getCurrentSession();

      if (!sessionResponse.success) {
        // No session exists, create one
        const createResponse = await bridgeRef.current.createSession(userId);
        if (!createResponse.success) {
          throw new Error(createResponse.error || 'Failed to create session');
        }
        setSession(createResponse.data);
      } else {
        setSession(sessionResponse.data);
      }

      // Load chat history if session exists
      if (sessionResponse.data) {
        const historyRequest: GetChatHistoryRequest = {
          sessionId: sessionResponse.data.session_id,
          requestingAgent: 'user',
          limit: 100,
        };
        const historyResponse = await bridgeRef.current.getChatHistory(historyRequest);

        if (historyResponse.success && historyResponse.data) {
          setMessages(historyResponse.data);
        }
      }

      setIsLoading(false);
    } catch (err) {
      console.error('[useSession] Initialization error:', err);
      setError(err instanceof Error ? err.message : 'Failed to initialize session');
      setIsLoading(false);
    }
  }, [userId]);

  /**
   * Send a message through the session
   */
  const sendMessage = useCallback(async (
    content: JsonValue,
    toAgent?: AgentId | 'user',
    priority: 'low' | 'normal' | 'high' | 'urgent' = 'normal'
  ) => {
    if (!session || !bridgeRef.current) {
      throw new Error('Session not initialized');
    }

    try {
      setError(null);

      // Sanitize content if it's a string (XSS protection)
      let sanitizedContent = content;
      if (typeof content === 'string') {
        // Basic HTML sanitization - strip script tags and event handlers
        sanitizedContent = content
          .replace(/<script\b[^<]*(?:(?!<\/script>)<[^<]*)*<\/script>/gi, '')
          .replace(/on\w+="[^"]*"/gi, '')
          .replace(/on\w+='[^']*'/gi, '');
      }

      const messageRequest: SendSessionMessageRequest = {
        sessionId: session.session_id,
        fromAgent: 'user',
        toAgent,
        content: sanitizedContent,
        priority,
      };

      const response = await bridgeRef.current.sendSessionMessage(messageRequest);

      if (!response.success) {
        throw new Error(response.error || 'Failed to send message');
      }

      // Message will be added to state via subscription event
    } catch (err) {
      console.error('[useSession] Send message error:', err);
      setError(err instanceof Error ? err.message : 'Failed to send message');
      throw err;
    }
  }, [session]);

  /**
   * Refresh messages from backend
   */
  const refreshMessages = useCallback(async () => {
    if (!session || !bridgeRef.current) return;

    try {
      const historyRequest: GetChatHistoryRequest = {
        sessionId: session.session_id,
        requestingAgent: 'user',
        limit: 100,
      };
      const response = await bridgeRef.current.getChatHistory(historyRequest);

      if (response.success && response.data) {
        setMessages(response.data);
      }
    } catch (err) {
      console.error('[useSession] Refresh messages error:', err);
    }
  }, [session]);

  /**
   * Set typing status (simulated for now - backend doesn't support typing_status signal yet)
   */
  const setTyping = useCallback((isTyping: boolean) => {
    isTypingRef.current = isTyping;

    // TODO: When backend supports typing_status signal:
    // bridgeRef.current?.emit('typing_status', {
    //   sessionId: session?.session_id,
    //   agent: 'user',
    //   isTyping
    // });
  }, []);

  /**
   * Reconnect to session
   */
  const reconnect = useCallback(async () => {
    await initializeSession();
  }, [initializeSession]);

  /**
   * Initialize on mount
   */
  useEffect(() => {
    if (autoConnect) {
      initializeSession();
    }

    return () => {
      // Cleanup
      if (pollIntervalRef.current) {
        clearInterval(pollIntervalRef.current);
      }
      // Note: We don't destroy the bridge here as it might be reused
    };
  }, [autoConnect, initializeSession]);

  /**
   * Poll for new messages (fallback until Tauri Events push is implemented)
   */
  useEffect(() => {
    if (session && pollInterval > 0) {
      pollIntervalRef.current = setInterval(() => {
        refreshMessages();
      }, pollInterval);

      return () => {
        if (pollIntervalRef.current) {
          clearInterval(pollIntervalRef.current);
        }
      };
    }
  }, [session, pollInterval, refreshMessages]);

  return {
    session,
    messages,
    isLoading,
    error,
    typingAgents,
    sendMessage,
    refreshMessages,
    setTyping,
    reconnect,
  };
}

