});

// Normalize API response to ChatMessage
const normalizeApiMessage = (data: any, channelType: 'private' | 'group'): ChatMessage => ({
  id: data.messageId || data.message_id || data.id || generateMessageId(),
  content: data.content || data.message || data.response || '',
  senderId: data.personaId || data.persona_id || data.agent_id || data.from_agent || 'system',
  senderName: data.senderName || data.sender_name || data.from_agent || 'System',
  senderType: data.from_agent === 'user' ? 'user' : 'persona',
  timestamp: data.createdAt || data.created_at || data.timestamp || new Date().toISOString(),
  channelType,
  personaId: data.personaId || data.persona_id || data.agent_id || data.from_agent || 'system',
});

const coreContentToString = (content: unknown): string => {
  if (typeof content === 'string') return content;
  if (content === null || content === undefined) return '';
  if (typeof content === 'object') {
    const candidate = content as Record<string, unknown>;
    if (typeof candidate.message === 'string') return candidate.message;
    if (typeof candidate.content === 'string') return candidate.content;
    return JSON.stringify(content);
  }
  return String(content);
};

