  async sendChatMessage(message: string): Promise<BridgeResponse<ChatMessage>> {
    const trimmed = message.trim();
    if (!trimmed)
      return {
        success: false,
        error: "Message cannot be empty",
        code: "CHAT_MESSAGE_EMPTY",
      };
    try {
      const sessionId = "session-default";
      const request = {
        user_id: this.userId,
        message: trimmed,
        session_id: sessionId,
        inference_context: {
          user_id: this.userId,
          agent_id: "alden",
          session_id: sessionId,
          data_classification: "internal",
          security_mode: "standard",
          allow_tool_execution: false,
          request_id: `alden-${Date.now()}-${Math.random().toString(36).slice(2, 8)}`,
          created_at: new Date().toISOString(),
        },
      };
      const response = await this.invokeCommand<ChatMessage>(
        "alden_chat_send",
        { request },
      );
      this.notifySubscribers({ type: "chat_message_sent", data: response });
      return { success: true, data: response };
    } catch (error: any) {
      // Enhanced error propagation - preserve Ollama-specific error messages
      const errorMsg = error.message || "Failed to send chat message";
      return {
        success: false,
        error: errorMsg,
        code: errorMsg.includes("Ollama")
          ? "OLLAMA_SERVICE_ERROR"
          : "CHAT_SEND_ERROR",
      };
    }
  }

