#[tauri::command]
pub async fn alden_chat_send(
    auth: State<'_, Arc<SecureAuthenticator>>,
    vault: State<'_, SecureVault>,
    request: AuthenticatedRequest<SendChatMessageRequest>,
    state: State<'_, ChatState>,
    settings_state: State<'_, SettingsState>,
) -> Result<ChatMessageEntry, String> {
    let authenticated_user = auth
        .verify_token(&request.auth_token)
        .await
        .map_err(|e| format!("Authentication failed: {e}"))?;

    if authenticated_user != request.payload.user_id {
        return Err("Unauthorized: Cannot send messages for other users".to_string());
    }
    let security_context = match resolve_inference_security_context(
        &authenticated_user,
        &request.payload.user_id,
        &request.payload.session_id,
        request.payload.inference_context.clone(),
    ) {
        Ok(ctx) => {
            let metadata = serde_json::json!({
                "agent_id": "alden",
                "session_id": ctx.session_id,
                "requested_security_mode": ctx.security_mode,
                "data_classification": ctx.data_classification
            });
            if let Err(e) = vault
                .log_audit_event(
                    &authenticated_user,
                    "inference_context_validate",
                    Some("alden_chat_send"),
                    "success",
                    None,
                    Some(metadata),
                )
                .await
            {
                eprintln!("[Alden Chat] Audit log failed: {e}");
            }
            ctx
        }
        Err(e) => {
            let metadata = serde_json::json!({
                "agent_id": "alden",
                "session_id": request.payload.session_id,
                "requested_security_mode": request
                    .payload
                    .inference_context
                    .as_ref()
                    .map(|ctx| &ctx.security_mode),
                "data_classification": request
                    .payload
                    .inference_context
                    .as_ref()
                    .map(|ctx| &ctx.data_classification)
            });
            if let Err(audit_err) = vault
                .log_audit_event(
                    &authenticated_user,
                    "inference_context_validate",
                    Some("alden_chat_send"),
                    "failure",
                    Some(&e),
                    Some(metadata),
                )
                .await
            {
                eprintln!("[Alden Chat] Audit log failed: {audit_err}");
            }
            return Err(e);
        }
    };

    let (effective_security_mode, downgraded) =
        if security_context.security_mode == InferenceSecurityMode::Confidential {
            if allow_confidential_downgrade() {
                let metadata = serde_json::json!({
                    "agent_id": "alden",
                    "session_id": security_context.session_id,
                    "requested_security_mode": "confidential",
                    "effective_security_mode": "standard",
                    "reason": "confidential_runtime_unavailable"
                });
                if let Err(e) = vault
                    .log_audit_event(
                        &authenticated_user,
                        "inference_security_mode_downgrade",
                        Some("alden_chat_send"),
                        "success",
                        None,
                        Some(metadata),
                    )
                    .await
                {
                    eprintln!("[Alden Chat] Audit log failed: {e}");
                }
                (InferenceSecurityMode::Standard, true)
            } else {
                let err =
                    "Confidential inference mode is not available for Alden runtime".to_string();
                let metadata = serde_json::json!({
                    "agent_id": "alden",
                    "session_id": security_context.session_id,
                    "requested_security_mode": "confidential",
                    "effective_security_mode": "rejected",
                    "reason": "confidential_runtime_unavailable"
                });
                if let Err(e) = vault
                    .log_audit_event(
                        &authenticated_user,
                        "inference_security_mode_rejected",
                        Some("alden_chat_send"),
                        "failure",
                        Some(&err),
                        Some(metadata),
                    )
                    .await
                {
                    eprintln!("[Alden Chat] Audit log failed: {e}");
                }
                return Err(err);
            }
        } else {
            (InferenceSecurityMode::Standard, false)
        };

    let trimmed = request.payload.message.trim();
    if trimmed.is_empty() {
        return Err("Message cannot be empty".to_string());
    }

    let mut user_message = ChatMessageEntry::new(
        &request.payload.user_id,
        "user",
        trimmed,
        &request.payload.session_id,
    );
    user_message.metadata = Some(serde_json::json!({
        "inference_security_context": &security_context,
        "inference_stage": "request_received",
        "inference_security_mode_effective": effective_security_mode,
        "inference_security_mode_downgraded": downgraded
    }));
    state
        .append_history(&request.payload.user_id, user_message)
        .await?;

    let history = state
        .load_history(&request.payload.user_id, Some(10))
        .await
        .unwrap_or_default();

    let settings = {
        let manager = settings_state
            .manager
            .lock()
            .map_err(|_| "Failed to lock settings manager")?;
        manager.load()
    };
    let model_override = settings
        .personality
        .alden_model
        .clone()
        .filter(|v| !v.trim().is_empty())
        .or_else(|| {
            let v = settings.models_runtime.default_model.clone();
            if v.trim().is_empty() {
                None
            } else {
                Some(v)
            }
        });

    let response_text = state
        .chat_manager
        .handle_message(
            HandleMessageRequest {
                user_id: request.payload.user_id.clone(),
                message: trimmed.to_string(),
                session_id: request.payload.session_id.clone(),
                inference_context: Some(security_context.clone()),
            },
            history,
            model_override,
        )
        .await?;

    let mut response_entry = ChatMessageEntry::new(
        &request.payload.user_id,
        "alden",
        &response_text,
        &request.payload.session_id,
    );
    response_entry.metadata = Some(serde_json::json!({
        "inference_security_context": &security_context,
        "inference_stage": "response_generated",
        "inference_security_mode_effective": effective_security_mode,
        "inference_security_mode_downgraded": downgraded
    }));
    state
        .append_history(&request.payload.user_id, response_entry.clone())
        .await?;

    Ok(response_entry)
}

