pub struct ChatManager {
    ollama_url: String,
    ollama_model: String,
}

impl ChatManager {
    pub fn new() -> Self {
        Self {
            ollama_url: "http://localhost:11434".to_string(),
            ollama_model: "llama3.2".to_string(), // Default model, overridden dynamically by model_router
        }
    }

    /// Check if Ollama service is running and accessible
    pub async fn check_ollama_health(&self) -> Result<Vec<String>, String> {
        let client = reqwest::Client::builder()
            .timeout(std::time::Duration::from_secs(5))
            .build()
            .map_err(|e| format!("Failed to create HTTP client: {e}"))?;

        let url = format!("{}/api/tags", self.ollama_url);

        match client.get(&url).send().await {
            Ok(response) => {
                if response.status().is_success() {
                    #[derive(serde::Deserialize)]
                    struct TagsResponse {
                        models: Vec<ModelInfo>,
                    }
                    #[derive(serde::Deserialize)]
                    struct ModelInfo {
                        name: String,
                    }

                    match response.json::<TagsResponse>().await {
                        Ok(tags) => {
                            let models: Vec<String> =
                                tags.models.iter().map(|m| m.name.clone()).collect();
                            if models.is_empty() {
                                Err(format!(
                                    "Ollama is running but no models installed. Run: ollama pull llama3.2"
                                ))
                            } else {
                                Ok(models)
                            }
                        }
                        Err(e) => Err(format!("Failed to parse Ollama response: {e}")),
                    }
                } else {
                    Err(format!("Ollama returned error: HTTP {}", response.status()))
                }
            }
            Err(e) => {
                if e.is_connect() {
                    Err(format!(
                        "Ollama is not running at {}. Start with: ollama serve",
                        self.ollama_url
                    ))
                } else if e.is_timeout() {
                    Err(format!("Ollama health check timed out after 5s"))
                } else {
                    Err(format!("Failed to connect to Ollama: {e}"))
                }
            }
        }
    }

