    /// Handle incoming chat message and generate response via Ollama
    pub async fn handle_message(
        &self,
        request: HandleMessageRequest,
        history: Vec<ChatMessageEntry>,
        model_override: Option<String>,
    ) -> Result<String, String> {
        let message = request.message.trim();

        if message.is_empty() {
            return Err("Message cannot be empty".to_string());
        }

        // Fail loud if Ollama is unavailable
        self.call_ollama(message, history, model_override).await
    }

    /// Call Ollama API for LLM response
    async fn call_ollama(
        &self,
        user_message: &str,
        history: Vec<ChatMessageEntry>,
        model_override: Option<String>,
    ) -> Result<String, String> {
        let client = reqwest::Client::builder()
            .timeout(std::time::Duration::from_secs(60))
            .build()
            .map_err(|e| format!("Failed to create HTTP client: {e}"))?;

        let model = resolve_model_for_persona("alden", model_override.as_deref());
        println!("[Alden Chat] Using model: {}", model);

        let mut messages = Vec::new();
        messages.push(OllamaMessage {
            role: "system".to_string(),
            content: self.get_system_prompt(),
        });

        let recent_history: Vec<_> = history.iter().take(10).rev().collect();
        for entry in recent_history {
            messages.push(OllamaMessage {
                role: if entry.role == "alden" {
                    "assistant".to_string()
                } else {
                    "user".to_string()
                },
                content: entry.content.clone(),
            });
        }

        messages.push(OllamaMessage {
            role: "user".to_string(),
            content: user_message.to_string(),
        });

        let request_body = OllamaChatRequest {
            model: model.clone(),
            messages,
            stream: false,
        };

        let url = format!("{}/api/chat", self.ollama_url);

        let response = client.post(&url).json(&request_body).send().await;

        let response = match response {
            Ok(res) => res,
            Err(e) if e.is_connect() => {
                println!("[Alden Chat] Ollama connection failed. Attempting to start service...");
                // Internal helper to start service
                let start_status = Command::new("ollama").arg("serve").spawn();

                match start_status {
                    Ok(_) => {
                        println!(
                            "[Alden Chat] Ollama service spawned. Waiting 5s for initialization..."
                        );
                        tokio::time::sleep(std::time::Duration::from_secs(5)).await;

                        // Retry the request once
                        client
                            .post(&url)
                            .json(&request_body)
                            .send()
                            .await
                            .map_err(|e| format!("Ollama failed even after restart attempt: {e}"))?
                    }
                    Err(spawn_err) => {
                        return Err(format!(
                            "Ollama is not running and failed to auto-start: {spawn_err}"
                        ));
                    }
                }
            }
            Err(e) => {
                if e.is_timeout() {
                    return Err(format!("Ollama request timed out after 60s. Model '{}' may be too large, busy, or not loaded.", model));
                } else {
                    return Err(format!("Failed to connect to Ollama: {e}"));
                }
            }
        };

        if !response.status().is_success() {
            let status = response.status();
            let error_text = response.text().await.unwrap_or_default();
            if status.as_u16() == 404 && error_text.contains("model") {
                return Err(format!(
                    "Model '{}' not found. Please run: ollama pull {}",
                    model, model
                ));
            }
            return Err(format!("Ollama API error ({status}): {error_text}"));
        }

        let ollama_response: OllamaChatResponse = response
            .json()
            .await
            .map_err(|e| format!("Failed to parse Ollama response: {e}"))?;

        Ok(ollama_response.message.content)
    }
}

