fn default_user_id() -> String {
    "default-user".to_string()
}

fn default_session_id() -> String {
    "session-default".to_string()
}

#[derive(Debug, Clone, Serialize, Deserialize, PartialEq, Eq)]
#[serde(rename_all = "snake_case")]
pub enum InferenceDataClassification {
    Public,
    Internal,
    Sensitive,
    HighlySensitive,
}

impl Default for InferenceDataClassification {
    fn default() -> Self {
        Self::Internal
    }
}

#[derive(Debug, Clone, Serialize, Deserialize, PartialEq, Eq)]
#[serde(rename_all = "snake_case")]
pub enum InferenceSecurityMode {
    Standard,
    Confidential,
}

impl Default for InferenceSecurityMode {
    fn default() -> Self {
        Self::Standard
    }
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct InferenceSecurityContext {
    pub user_id: String,
    pub agent_id: String,
    pub session_id: String,
    #[serde(default)]
    pub data_classification: InferenceDataClassification,
    #[serde(default)]
    pub security_mode: InferenceSecurityMode,
    #[serde(default)]
    pub allow_tool_execution: bool,
    pub request_id: String,
    pub created_at: String,
}

fn resolve_inference_security_context(
    authenticated_user: &str,
    request_user_id: &str,
    request_session_id: &str,
    inference_context: Option<InferenceSecurityContext>,
) -> Result<InferenceSecurityContext, String> {
    if request_session_id.trim().is_empty() {
        return Err("Inference security context requires a non-empty session_id".to_string());
    }

    let mut context = inference_context.unwrap_or_else(|| InferenceSecurityContext {
        user_id: request_user_id.to_string(),
        agent_id: "alden".to_string(),
        session_id: request_session_id.to_string(),
        data_classification: InferenceDataClassification::Internal,
        security_mode: InferenceSecurityMode::Standard,
        allow_tool_execution: false,
        request_id: Uuid::new_v4().to_string(),
        created_at: Utc::now().to_rfc3339(),
    });

    if context.user_id != authenticated_user || context.user_id != request_user_id {
        return Err("Inference security context user_id does not match authenticated user".to_string());
    }
    if context.agent_id != "alden" {
        return Err("Inference security context agent_id must be 'alden'".to_string());
    }
    if context.session_id != request_session_id {
        return Err(
            "Inference security context session_id does not match request session_id".to_string(),
        );
    }
    if context.request_id.trim().is_empty() {
        context.request_id = Uuid::new_v4().to_string();
    }
    if context.created_at.trim().is_empty() {
        context.created_at = Utc::now().to_rfc3339();
    }

    Ok(context)
}

fn allow_confidential_downgrade() -> bool {
    std::env::var("HEARTHLINK_ALLOW_CONFIDENTIAL_DOWNGRADE")
        .map(|value| matches!(value.to_ascii_lowercase().as_str(), "1" | "true" | "yes" | "on"))
        .unwrap_or(false)
}

