        .filter(|value| !value.trim().is_empty())
        .or_else(|| {
            let value = settings.models_runtime.default_model.clone();
            if value.trim().is_empty() {
                None
            } else {
                Some(value)
            }
        });

    let response_text = state
        .chat_manager
        .handle_message(
            HandleMessageRequest {
                user_id: request.payload.user_id.clone(),
                message: trimmed.to_string(),
                session_id: request.payload.session_id.clone(),
            },
            history,
            persona_mode,
            model_override,
        )
        .await?;

    let mut response_entry = ChatMessageEntry::new(
        &request.payload.user_id,
        "mimic",
        &response_text,
        &request.payload.session_id,
    );
    response_entry.metadata = Some(serde_json::json!({
        "inference_security_context": &security_context,
        "inference_stage": "response_generated",
        "inference_security_mode_effective": effective_security_mode,
        "inference_security_mode_downgraded": downgraded
    }));
    state
        .append_history(&request.payload.user_id, response_entry.clone())
        .await?;

    Ok(response_entry)
}

/// Retrieve chat history for Mimic (includes context from other agents)
pub async fn mimic_chat_history(
    auth: State<'_, Arc<SecureAuthenticator>>,
    request: AuthenticatedRequest<ChatHistoryRequest>,
    state: State<'_, MimicChatState>,
) -> Result<Vec<ChatMessageEntry>, String> {
    let authenticated_user = auth
        .verify_token(&request.auth_token)
        .await
        .map_err(|e| format!("Authentication failed: {e}"))?;

    if authenticated_user != request.payload.user_id {
        return Err("Unauthorized: Cannot access chat history for other users".to_string());
    }

    state
        .load_history(&request.payload.user_id, request.payload.limit)
        .await
}

/// Clear Mimic's chat history
pub async fn mimic_chat_clear(
    auth: State<'_, Arc<SecureAuthenticator>>,
    request: AuthenticatedRequest<ClearChatRequest>,
    state: State<'_, MimicChatState>,
) -> Result<(), String> {
    let authenticated_user = auth
        .verify_token(&request.auth_token)
        .await
        .map_err(|e| format!("Authentication failed: {e}"))?;

    if authenticated_user != request.payload.user_id {
        return Err("Unauthorized: Cannot clear chat history for other users".to_string());
    }

    state.clear_history(&request.payload.user_id).await
}

