{"command":"$path = 'd:/Myth-TechForge/MythologIQ/Design Documents/MythologIQ Labs-Business Plan.txt'
$lines = Get-Content -Path $path
$out = New-Object System.Collections.Generic.List[string]
$constraint = '- Constraint: preserve MythologIQ as parent lab brand and keep continuity with existing artifacts: `planned`'
$anchor = '- Legal identity anchor: all corporate references standardize to MythologIQ Labs, LLC: `planned`'
$hasAnchor = $lines -contains $anchor
foreach ($line in $lines) {
  $updated = $line -replace 'MythologIQ LLC', 'MythologIQ Labs, LLC'
  $out.Add($updated)
  if (-not $hasAnchor -and $updated -eq $constraint) {
    $out.Add($anchor)
  }
}
Set-Content -Path $path -Value $out"}

