// AccessibilityBar.tsx â€“ Enhanced accessibility control with improved UX and WCAG compliance

import React, { useState, useEffect, useRef } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { useAccessibility } from '../context/AccessibilityContext';
import { Switch } from '../styles/ui/switch';
import { Button } from '../styles/ui/button';

interface AccessibilityOption {
  id: keyof Omit<ReturnType<typeof useAccessibility>, 'toggleSetting'>;
  label: string;
  description: string;
  icon: string;
}

const accessibilityOptions: AccessibilityOption[] = [
  {
    id: 'highContrast',
    label: 'High Contrast',
    description: 'Increases color contrast for better visibility',
    icon: 'ðŸŒ—'
  },
  {
    id: 'dyslexiaFont',
    label: 'Dyslexia-Friendly Font',
    description: 'Uses OpenDyslexic font for easier reading',
    icon: 'ðŸ“–'
  },
  {
    id: 'reduceMotion',
    label: 'Reduce Motion',
    description: 'Minimizes animations and transitions',
    icon: 'ðŸŽ¯'
  },
  {
    id: 'plainLanguage',
    label: 'Plain Language',
    description: 'Simplifies text and removes decorative fonts',
    icon: 'ðŸ“'
  }
];

const AccessibilityBar = () => {
  const [isExpanded, setIsExpanded] = useState(false);
  const [isVisible, setIsVisible] = useState(true);
  const panelRef = useRef<HTMLDivElement>(null);
  const triggerRef = useRef<HTMLButtonElement>(null);
  
  const {
    highContrast,
    dyslexiaFont,
    reduceMotion,
    plainLanguage,
    toggleSetting
  } = useAccessibility();

  // Keyboard navigation
  useEffect(() => {
    const handleKeyDown = (event: KeyboardEvent) => {
      // Global shortcut Ctrl+Shift+A to open accessibility panel
      if (event.ctrlKey && event.shiftKey && event.key === 'A') {
        event.preventDefault();
        setIsExpanded(!isExpanded);
        if (!isExpanded) {
          setTimeout(() => panelRef.current?.focus(), 100);
        }
      }
      
      // Escape to close
      if (event.key === 'Escape' && isExpanded) {
        setIsExpanded(false);
        triggerRef.current?.focus();
      }
    };

    document.addEventListener('keydown', handleKeyDown);
    return () => document.removeEventListener('keydown', handleKeyDown);
  }, [isExpanded]);

  // Click outside to close
  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      if (panelRef.current && !panelRef.current.contains(event.target as Node) &&
          triggerRef.current && !triggerRef.current.contains(event.target as Node)) {
        setIsExpanded(false);
      }
    };

    if (isExpanded) {
      document.addEventListener('mousedown', handleClickOutside);
    }
    
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, [isExpanded]);

  const handleToggleSetting = (setting: keyof Omit<ReturnType<typeof useAccessibility>, 'toggleSetting'>) => {
    toggleSetting(setting);
    // Announce change to screen readers
    const option = accessibilityOptions.find(opt => opt.id === setting);
    const newState = setting === 'highContrast' ? !highContrast :
                    setting === 'dyslexiaFont' ? !dyslexiaFont :
                    setting === 'reduceMotion' ? !reduceMotion : !plainLanguage;
    
    // Create temporary announcement for screen readers
    const announcement = document.createElement('div');
    announcement.setAttribute('aria-live', 'polite');
    announcement.setAttribute('aria-atomic', 'true');
    announcement.className = 'sr-only';
    announcement.textContent = `${option?.label} ${newState ? 'enabled' : 'disabled'}`;
    document.body.appendChild(announcement);
    setTimeout(() => document.body.removeChild(announcement), 1000);
  };

  const activeCount = [highContrast, dyslexiaFont, reduceMotion, plainLanguage].filter(Boolean).length;

  if (!isVisible) return null;

  return (
    <>
      {/* Screen reader only instructions */}
      <div className="sr-only">
        <p>Accessibility settings available. Press Ctrl+Shift+A to open accessibility panel, or use the accessibility button in the top right corner.</p>
      </div>

      <div className="fixed top-4 right-4 z-50 flex flex-col items-end gap-2">
        {/* Floating Trigger Button */}
        <Button
          ref={triggerRef}
          variant="outline"
          size="icon"
          onClick={() => setIsExpanded(!isExpanded)}
          className={`
            w-16 h-16 rounded-full shadow-xl transition-all duration-300 ease-in-out
            bg-black/40 border-2 border-amber-400/25 hover:bg-black/50 hover:border-amber-300/40
            focus:ring-2 focus:ring-amber-400 focus:ring-offset-2 focus:ring-offset-zinc-900
            ${isExpanded ? 'bg-black/60 border-amber-300/50 shadow-amber-400/15' : ''}
            ${activeCount > 0 ? 'ring-2 ring-emerald-400/60' : ''}
          `}
          aria-label={`Accessibility settings${activeCount > 0 ? ` (${activeCount} active)` : ''}. Press Ctrl+Shift+A or click to ${isExpanded ? 'close' : 'open'}`}
          aria-expanded={isExpanded}
          aria-haspopup="dialog"
          title={`Accessibility Settings${activeCount > 0 ? ` (${activeCount} active)` : ''}`}
        >
          <img 
            src="/assets/Accessibility.png" 
            alt="" 
            className="w-10 h-10 object-contain" 
            aria-hidden="true"
            style={{ 
              filter: isExpanded ? 'brightness(0) invert(1)' : 'brightness(0) invert(1) sepia(1) saturate(2) hue-rotate(10deg)',
              transform: 'scale(1.1)'
            }}
          />
          {activeCount > 0 && (
            <span className="absolute -top-1 -right-1 w-5 h-5 bg-emerald-500 text-white text-xs rounded-full flex items-center justify-center font-bold">
              {activeCount}
            </span>
          )}
        </Button>

        {/* Accessibility Panel */}
        <AnimatePresence>
          {isExpanded && (
            <motion.div
              ref={panelRef}
              role="dialog"
              aria-labelledby="accessibility-title"
              aria-describedby="accessibility-description"
              tabIndex={-1}
              initial={{ opacity: 0, scale: 0.95, y: -10 }}
              animate={{ opacity: 1, scale: 1, y: 0 }}
              exit={{ opacity: 0, scale: 0.95, y: -10 }}
              transition={{ duration: 0.2, ease: 'easeOut' }}
