// AccessibilityContext.tsx â€“ Global state for accessibility toggles
// Must be located in /context

import React, { createContext, useContext, useState, useEffect } from 'react';

interface AccessibilitySettings {
  highContrast: boolean;
  dyslexiaFont: boolean;
  reduceMotion: boolean;
  plainLanguage: boolean;
  toggleSetting: (setting: keyof Omit<AccessibilitySettings, 'toggleSetting'>) => void;
}

const defaultSettings: AccessibilitySettings = {
  highContrast: false,
  dyslexiaFont: false,
  reduceMotion: false,
  plainLanguage: false,
  toggleSetting: () => {}
};

const AccessibilityContext = createContext<AccessibilitySettings>(defaultSettings);

export const useAccessibility = () => useContext(AccessibilityContext);

export const AccessibilityProvider = ({ children }: { children: React.ReactNode }) => {
  const [settings, setSettings] = useState<Omit<AccessibilitySettings, 'toggleSetting'>>({
    highContrast: false,
    dyslexiaFont: false,
    reduceMotion: false,
    plainLanguage: false
  });

  const toggleSetting = (setting: keyof typeof settings) => {
    setSettings(prev => {
      const updated = { ...prev, [setting]: !prev[setting] };
      localStorage.setItem('accessibility', JSON.stringify(updated));
      return updated;
    });
  };

  useEffect(() => {
    const stored = localStorage.getItem('accessibility');
    if (stored) {
      try {
        const parsed = JSON.parse(stored);
        setSettings(parsed);
      } catch {
        console.warn('[âš ï¸] Failed to parse accessibility settings from localStorage.');
      }
    }
  }, []);

  useEffect(() => {
    const body = document.body;
    body.classList.toggle('accessibility-dyslexia', settings.dyslexiaFont);
    body.classList.toggle('accessibility-contrast', settings.highContrast);
    body.classList.toggle('accessibility-reduced-motion', settings.reduceMotion);
    body.classList.toggle('accessibility-plain', settings.plainLanguage);
  }, [settings]);

  return (
    <AccessibilityContext.Provider value={{ ...settings, toggleSetting }}>
      {children}
    </AccessibilityContext.Provider>
  );
};

