/**
 * Feature Flags Configuration
 * Sprint K Implementation
 * 
 * Centralized feature toggle system for gradual rollout and A/B testing
 * All flags default to false for safety (opt-in model)
 */

export interface FeatureFlags {
  // Accessibility & UI Features
  calmMode: boolean;           // Reduced animations, simplified UI
  narration: boolean;          // Audio descriptions for tools
  highContrast: boolean;       // Enhanced visual contrast
  reducedMotion: boolean;      // Respect prefers-reduced-motion

  // Platform Features
  guardianPortal: boolean;     // Parent/guardian access controls
  exports: boolean;           // Admin export functionality (S1)
  musterSet: boolean;         // Veteran-specific content
  advancedTools: boolean;     // Beta/experimental tools

  // Development & Testing
  mockData: boolean;          // Use mock data instead of real APIs
  performanceMonitoring: boolean; // Enable performance tracking
  errorReporting: boolean;    // Enhanced error reporting

  // Future S1+ Features (Preparatory)
  supabaseIntegration: boolean; // Database integration
  userAccounts: boolean;      // User authentication system
  socialFeatures: boolean;    // Community interactions
  aiIntegration: boolean;     // AI-powered features
}

/**
 * Default feature flag values
 * Conservative defaults - all flags off by default
 */
const defaultFlags: FeatureFlags = {
  // Accessibility & UI Features
  calmMode: false,
  narration: false,
  highContrast: false,
  reducedMotion: false,

  // Platform Features  
  guardianPortal: false,
  exports: false,
  musterSet: true,  // Exception: Veteran content enabled by default
  advancedTools: false,

  // Development & Testing
  mockData: true,   // Exception: Safe default for development
  performanceMonitoring: false,
  errorReporting: false,

  // Future Features
  supabaseIntegration: false,
  userAccounts: false,
  socialFeatures: false,
  aiIntegration: false,
};

/**
 * Environment variable mapping
 * Maps environment variables to feature flag keys
 */
const envMapping: Record<keyof FeatureFlags, string> = {
  // Accessibility & UI Features
  calmMode: 'REACT_APP_CALM_MODE',
  narration: 'REACT_APP_NARRATION',
  highContrast: 'REACT_APP_HIGH_CONTRAST',
  reducedMotion: 'REACT_APP_REDUCED_MOTION',

  // Platform Features
  guardianPortal: 'REACT_APP_GUARDIAN_PORTAL',
  exports: 'REACT_APP_EXPORTS',
  musterSet: 'REACT_APP_MUSTER_SET',
  advancedTools: 'REACT_APP_ADVANCED_TOOLS',

  // Development & Testing
  mockData: 'REACT_APP_MOCK_DATA',
  performanceMonitoring: 'REACT_APP_PERFORMANCE_MONITORING',
  errorReporting: 'REACT_APP_ERROR_REPORTING',

  // Future Features
  supabaseIntegration: 'REACT_APP_SUPABASE_INTEGRATION',
  userAccounts: 'REACT_APP_USER_ACCOUNTS',
  socialFeatures: 'REACT_APP_SOCIAL_FEATURES',
  aiIntegration: 'REACT_APP_AI_INTEGRATION',
};

/**
 * Parse environment variable to boolean
 * Supports: true, false, 1, 0, yes, no (case insensitive)
 */
function parseEnvBoolean(value: string | undefined, defaultValue: boolean): boolean {
  if (!value) return defaultValue;
  
  const normalized = value.toLowerCase().trim();
  
  // Explicit true values
  if (['true', '1', 'yes', 'on', 'enabled'].includes(normalized)) {
    return true;
  }
  
  // Explicit false values
  if (['false', '0', 'no', 'off', 'disabled'].includes(normalized)) {
    return false;
  }
  
  // Invalid values default to provided default
  console.warn(`Invalid feature flag value: "${value}". Using default: ${defaultValue}`);
  return defaultValue;
}

/**
 * Load feature flags from environment variables
 * Falls back to defaults for any missing or invalid values
 */
function loadFeatureFlags(): FeatureFlags {
  const flags: FeatureFlags = {} as FeatureFlags;
  
  // Load each flag from environment or use default
  for (const [flagKey, envKey] of Object.entries(envMapping)) {
    const envValue = process.env[envKey];
    const defaultValue = defaultFlags[flagKey as keyof FeatureFlags];
    
    flags[flagKey as keyof FeatureFlags] = parseEnvBoolean(envValue, defaultValue);
  }
  
  return flags;
}

/**
 * Current feature flags instance
 * Loaded once at module initialization
 */
export const featureFlags: FeatureFlags = loadFeatureFlags();

/**
 * Utility function to check if a feature is enabled
 * Provides a clean API for components
 */
export function isFeatureEnabled(flag: keyof FeatureFlags): boolean {
  return featureFlags[flag];
}

/**
 * Utility function to get multiple feature flags
 * Useful for complex feature combinations
 */
export function getFeatureFlags(...flags: (keyof FeatureFlags)[]): Record<string, boolean> {
  const result: Record<string, boolean> = {};
  
  for (const flag of flags) {
    result[flag] = featureFlags[flag];
  }
  
  return result;
}

/**
 * Development helper: Log current feature flag state
 * Only logs in development mode
 */
export function logFeatureFlags(): void {
  if (process.env.NODE_ENV === 'development') {
    console.group('ðŸŽ›ï¸ Feature Flags');
    console.table(featureFlags);
    console.groupEnd();
  }
}

