 */
export const platform = {
  isGuardianPortalEnabled: () => isFeatureEnabled('guardianPortal'),
  areExportsEnabled: () => isFeatureEnabled('exports'),
  isMusterSetEnabled: () => isFeatureEnabled('musterSet'),
  areAdvancedToolsEnabled: () => isFeatureEnabled('advancedTools'),
  
  // Check if user-facing features are available
  hasUserFeatures: () =>
    isFeatureEnabled('guardianPortal') || 
    isFeatureEnabled('userAccounts'),
};

/**
 * Development-specific feature flag checks
 * Useful for conditional development features
 */
export const development = {
  shouldUseMockData: () => isFeatureEnabled('mockData'),
  isPerformanceMonitoringEnabled: () => isFeatureEnabled('performanceMonitoring'),
  isErrorReportingEnabled: () => isFeatureEnabled('errorReporting'),
  
  // Check if any development features are enabled
  hasDevFeatures: () =>
    isFeatureEnabled('mockData') ||
    isFeatureEnabled('performanceMonitoring') ||
    isFeatureEnabled('errorReporting'),
};

// Initialize feature flags logging in development
if (process.env.NODE_ENV === 'development') {
  logFeatureFlags();
}

