};

const AccessibilityPanel: React.FC = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [settings, setSettings] = useState<LocalAccessibilitySettings>({
    highContrast: false,
    reducedMotion: false,
    fontSize: 'medium',
    voiceNavigation: false,
    keyboardShortcuts: true,
    dyslexiaFont: false,
    plainLanguage: false
  });
  const [isLoading, setIsLoading] = useState(true);
  const [isSaving, setIsSaving] = useState(false);
  const [isVoiceListening, setIsVoiceListening] = useState(false);
  const recognitionRef = useRef<any>(null);
  const [lastCommand, setLastCommand] = useState<string>('');

  // Load saved settings on mount
  useEffect(() => {
    const loadSettings = async () => {
      setIsLoading(true);
      try {
        const dbSettings = await loadAccessibilitySettings();
        const localSettings: LocalAccessibilitySettings = {
          highContrast: dbSettings.high_contrast,
          reducedMotion: dbSettings.reduced_motion,
          fontSize: dbSettings.font_size,
          voiceNavigation: dbSettings.voice_navigation,
          keyboardShortcuts: dbSettings.keyboard_shortcuts,
          dyslexiaFont: dbSettings.dyslexia_font,
          plainLanguage: dbSettings.plain_language,
        };
        setSettings(localSettings);
        applySettings(localSettings);
      } catch (error) {
        console.warn('Failed to load accessibility settings:', error);
      } finally {
        setIsLoading(false);
      }
    };
    
    loadSettings();

    // Check for system preferences
    const reducedMotionQuery = window.matchMedia('(prefers-reduced-motion: reduce)');
    const highContrastQuery = window.matchMedia('(prefers-contrast: high)');
    
    if (reducedMotionQuery.matches) {
      updateSetting('reducedMotion', true);
    }
    
    if (highContrastQuery.matches) {
      updateSetting('highContrast', true);
    }

    // Setup keyboard shortcuts
    const handleKeyboard = (e: KeyboardEvent) => {
      if (!settings.keyboardShortcuts) return;

      // Alt + A to open accessibility panel
      if (e.altKey && e.key === 'a') {
        e.preventDefault();
        setIsOpen(prev => !prev);
      }
      
      // Ctrl + Shift + A alternative shortcut (compatibility with AccessibilityBar)
      if (e.ctrlKey && e.shiftKey && e.key === 'A') {
        e.preventDefault();
        setIsOpen(prev => !prev);
      }
      
      // Alt + C for high contrast
      if (e.altKey && e.key === 'c') {
        e.preventDefault();
        updateSetting('highContrast', !settings.highContrast);
      }
      
      // Alt + V for voice navigation
      if (e.altKey && e.key === 'v') {
        e.preventDefault();
        toggleVoiceNavigation();
      }
      
      // Escape to close panel
      if (e.key === 'Escape' && isOpen) {
        setIsOpen(false);
      }
    };

    document.addEventListener('keydown', handleKeyboard);
    return () => document.removeEventListener('keydown', handleKeyboard);
  }, [settings.keyboardShortcuts, settings.highContrast, isOpen]);

  // Apply settings to document
  const applySettings = (newSettings: LocalAccessibilitySettings) => {
    const root = document.documentElement;
    
    // High contrast
    if (newSettings.highContrast) {
      root.classList.add('high-contrast');
    } else {
      root.classList.remove('high-contrast');
    }
    
    // Font size
    root.classList.remove('font-small', 'font-medium', 'font-large', 'font-xl');
    root.classList.add(`font-${newSettings.fontSize}`);
    
    // Reduced motion
    if (newSettings.reducedMotion) {
      root.style.setProperty('--animation-duration', '0ms');
    } else {
      root.style.removeProperty('--animation-duration');
    }
    
    // Dyslexia font
    if (newSettings.dyslexiaFont) {
      root.classList.add('accessibility-dyslexia');
    } else {
      root.classList.remove('accessibility-dyslexia');
    }
    
    // Plain language mode
    if (newSettings.plainLanguage) {
      root.classList.add('accessibility-plain');
    } else {
      root.classList.remove('accessibility-plain');
    }
  };

  const updateSetting = <K extends keyof LocalAccessibilitySettings>(
    key: K,
    value: LocalAccessibilitySettings[K]
  ) => {
    const newSettings = { ...settings, [key]: value };
    setSettings(newSettings);
    applySettings(newSettings);
    
    // Save to database/localStorage asynchronously
    saveSetting(newSettings);
  };

  const saveSetting = async (localSettings: LocalAccessibilitySettings) => {
    setIsSaving(true);
    try {
      // Convert to database format
      const dbSettings = {
        high_contrast: localSettings.highContrast,
        reduced_motion: localSettings.reducedMotion,
        font_size: localSettings.fontSize,
        voice_navigation: localSettings.voiceNavigation,
        keyboard_shortcuts: localSettings.keyboardShortcuts,
        dyslexia_font: localSettings.dyslexiaFont,
        plain_language: localSettings.plainLanguage,
      };
      
      await saveAccessibilitySettings(dbSettings);
    } catch (error) {
      console.warn('Failed to save accessibility settings:', error);
    } finally {
      setIsSaving(false);
    }
  };

  // Voice navigation setup
  const initVoiceNavigation = () => {
    if (!('webkitSpeechRecognition' in window) && !('SpeechRecognition' in window)) {
      alert('Voice navigation is not supported in your browser.');
      return false;
    }

    const SpeechRecognition = (window as any).SpeechRecognition || (window as any).webkitSpeechRecognition;
    recognitionRef.current = new SpeechRecognition();
    
    recognitionRef.current.continuous = true;
    recognitionRef.current.interimResults = false;
    recognitionRef.current.lang = 'en-US';

    recognitionRef.current.onresult = (event: any) => {
      const command = event.results[event.results.length - 1][0].transcript.toLowerCase().trim();
      setLastCommand(command);
      processVoiceCommand(command);
    };

    recognitionRef.current.onerror = (event: any) => {
      console.error('Voice recognition error:', event.error);
      setIsVoiceListening(false);
    };

    recognitionRef.current.onend = () => {
      if (settings.voiceNavigation) {
        // Restart if voice nav is still enabled
        setTimeout(() => {
          if (settings.voiceNavigation) {
            recognitionRef.current?.start();
          }
        }, 100);
      } else {
        setIsVoiceListening(false);
      }
    };

    return true;
  };

  const toggleVoiceNavigation = () => {
    if (!settings.voiceNavigation) {
      if (initVoiceNavigation()) {
        updateSetting('voiceNavigation', true);
