  const processVoiceCommand = (command: string) => {
    // Navigation commands
    const navCommands = [
      { patterns: ['go home', 'home page', 'main page'], path: '/' },
      { patterns: ['go to insight', 'insight page', 'tools'], path: '/insight' },
      { patterns: ['go to presence', 'presence page'], path: '/presence' },
