import React, { createContext, useContext, useState, useEffect, useCallback } from 'react';
import { useScreenReaderDetection } from '../hooks/useScreenReaderDetection';

interface AudioSettings {
  rate: number;
  pitch: number;
  volume: number;
  voice?: SpeechSynthesisVoice;
}

interface AudioAccessibilityContextType {
  isAudioMode: boolean;
  setAudioMode: (enabled: boolean) => void;
  screenReaderDetected: boolean;
  speak: (text: string, options?: Partial<AudioSettings> & { 
    priority?: 'low' | 'medium' | 'high';
    interrupt?: boolean;
    delay?: number;
  }) => Promise<void>;
  stopSpeaking: () => void;
  announcePageChange: (pageName: string) => void;
  announceStatusChange: (status: string) => void;
  isSupported: boolean;
  preferredSettings: AudioSettings;
  updateSettings: (settings: Partial<AudioSettings>) => void;
}

const AudioAccessibilityContext = createContext<AudioAccessibilityContextType | undefined>(undefined);

export const useAudioAccessibility = () => {
  const context = useContext(AudioAccessibilityContext);
  if (!context) {
    throw new Error('useAudioAccessibility must be used within AudioAccessibilityProvider');
  }
  return context;
};

interface AudioAccessibilityProviderProps {
  children: React.ReactNode;
}

export const AudioAccessibilityProvider: React.FC<AudioAccessibilityProviderProps> = ({ children }) => {
  const { isDetected: screenReaderDetected } = useScreenReaderDetection();
  const [isAudioMode, setIsAudioMode] = useState(false);
  const [isSupported, setIsSupported] = useState(false);
  const [preferredSettings, setPreferredSettings] = useState<AudioSettings>({
    rate: 0.9,
    pitch: 1.0,
    volume: 0.8
  });

  useEffect(() => {
    // Check if speech synthesis is supported
    setIsSupported('speechSynthesis' in window);
    
    // Only enable audio mode if explicitly stored as true in localStorage
    // Don't auto-enable based on screen reader detection
    const storedPreference = localStorage.getItem('mythologiq_audio_mode');
    if (storedPreference === 'true') {
      setIsAudioMode(true);
    } else {
      // Default to disabled, even with screen reader
      setIsAudioMode(false);
      // Clear any existing preference to ensure fresh start
      localStorage.removeItem('mythologiq_audio_mode');
    }

    // Load user's preferred audio settings
    const savedSettings = localStorage.getItem('mythologiq_audio_settings');
    if (savedSettings) {
      try {
        const settings = JSON.parse(savedSettings);
        setPreferredSettings(prev => ({ ...prev, ...settings }));
      } catch (error) {
        console.warn('Failed to load audio settings:', error);
      }
    }
  }, [screenReaderDetected]);

  const setAudioMode = useCallback((enabled: boolean) => {
    setIsAudioMode(enabled);
    localStorage.setItem('mythologiq_audio_mode', enabled.toString());
    
    if (enabled) {
      // Welcome message when enabling audio mode
      setTimeout(() => {
        speak("Audio mode enabled. You can now navigate MythologIQ with voice guidance. Press Alt+A to access audio settings.", 
          { priority: 'high' });
      }, 500);
    }
  }, []);

  const speak = useCallback((
    text: string, 
    options: Partial<AudioSettings> & { 
      priority?: 'low' | 'medium' | 'high';
      interrupt?: boolean;
      delay?: number;
    } = {}
  ): Promise<void> => {
    return new Promise((resolve, reject) => {
      if (!isSupported || !isAudioMode) {
        resolve();
        return;
      }

      // Handle interruption
      if (options.interrupt && window.speechSynthesis.speaking) {
        window.speechSynthesis.cancel();
      }

      // Apply delay if specified
      const executeSpeak = () => {
        const utterance = new SpeechSynthesisUtterance(text);
        
        // Apply settings
        utterance.rate = options.rate || preferredSettings.rate;
        utterance.pitch = options.pitch || preferredSettings.pitch;
        utterance.volume = options.volume || preferredSettings.volume;
        
        if (options.voice || preferredSettings.voice) {
          utterance.voice = options.voice || preferredSettings.voice!;
        }

        utterance.onend = () => resolve();
        utterance.onerror = (event) => reject(event.error);

        window.speechSynthesis.speak(utterance);
      };

      if (options.delay) {
        setTimeout(executeSpeak, options.delay);
      } else {
        executeSpeak();
      }
    });
  }, [isSupported, isAudioMode, preferredSettings]);

  const stopSpeaking = useCallback(() => {
    if (isSupported) {
      window.speechSynthesis.cancel();
    }
  }, [isSupported]);

  const announcePageChange = useCallback((pageName: string) => {
    speak(`Navigated to ${pageName}`, { priority: 'medium', interrupt: true });
  }, [speak]);

  const announceStatusChange = useCallback((status: string) => {
    speak(status, { priority: 'medium' });
  }, [speak]);

  const updateSettings = useCallback((settings: Partial<AudioSettings>) => {
    setPreferredSettings(prev => {
      const newSettings = { ...prev, ...settings };
      localStorage.setItem('mythologiq_audio_settings', JSON.stringify(newSettings));
      return newSettings;
    });
  }, []);

  const value: AudioAccessibilityContextType = {
    isAudioMode,
    setAudioMode,
    screenReaderDetected,
    speak,
    stopSpeaking,
    announcePageChange,
    announceStatusChange,
    isSupported,
    preferredSettings,
    updateSettings
  };

  return (
    <AudioAccessibilityContext.Provider value={value}>
      {children}
    </AudioAccessibilityContext.Provider>
  );
};

