/**
 * Accessibility Settings Service
 * Sprint S1 - Database integration with offline-first approach
 * 
 * Handles saving/loading accessibility settings with:
 * - Database sync for authenticated users
 * - localStorage fallback for offline/anonymous users
 * - Conflict resolution and error handling
 */

import { supabase, isSupabaseAvailable, getCurrentUser, AccessibilitySettings } from '../lib/supabase';

const STORAGE_KEY = 'accessibilitySettings';

/**
 * Default accessibility settings
 */
export const defaultSettings: AccessibilitySettings = {
  high_contrast: false,
  reduced_motion: false,
  font_size: 'medium',
  voice_navigation: false,
  keyboard_shortcuts: true,
  dyslexia_font: false,
  plain_language: false,
};

/**
 * Load accessibility settings
 * Priority: Database > localStorage > defaults
 */
export async function loadAccessibilitySettings(): Promise<AccessibilitySettings> {
  try {
    // Try to load from database if user is authenticated
    if (isSupabaseAvailable()) {
      const user = await getCurrentUser();
      if (user) {
        const dbSettings = await loadFromDatabase(user.id);
        if (dbSettings) {
          // Sync to localStorage as backup
          saveToLocalStorage(dbSettings);
          return dbSettings;
        }
      }
    }
    
    // Fallback to localStorage
    const localSettings = loadFromLocalStorage();
    if (localSettings) {
      return localSettings;
    }
    
    // Final fallback to defaults
    return defaultSettings;
  } catch (error) {
    console.warn('Error loading accessibility settings:', error);
    return loadFromLocalStorage() || defaultSettings;
  }
}

/**
 * Save accessibility settings
 * Saves to both database and localStorage for reliability
 */
export async function saveAccessibilitySettings(settings: AccessibilitySettings): Promise<void> {
  try {
    // Always save to localStorage first (immediate feedback)
    saveToLocalStorage(settings);
    
    // Try to save to database if user is authenticated
    if (isSupabaseAvailable()) {
      const user = await getCurrentUser();
      if (user) {
        await saveToDatabase(user.id, settings);
      }
    }
  } catch (error) {
    console.warn('Error saving accessibility settings:', error);
    // Settings are still saved to localStorage, so partial success
  }
}

/**
 * Load settings from database
 */
async function loadFromDatabase(userId: string): Promise<AccessibilitySettings | null> {
  if (!supabase) return null;
  
  try {
    const { data, error } = await supabase
      .from('user_accessibility_settings')
      .select('*')
      .eq('user_id', userId)
      .single();
    
    if (error) {
      if (error.code === 'PGRST116') {
        // No settings found - this is normal for new users
        return null;
      }
      throw error;
    }
    
    return {
      high_contrast: data.high_contrast,
      reduced_motion: data.reduced_motion,
      font_size: data.font_size,
      voice_navigation: data.voice_navigation,
      keyboard_shortcuts: data.keyboard_shortcuts,
      dyslexia_font: data.dyslexia_font,
      plain_language: data.plain_language,
    };
  } catch (error) {
    console.warn('Error loading from database:', error);
    return null;
  }
}

/**
 * Save settings to database
 */
async function saveToDatabase(userId: string, settings: AccessibilitySettings): Promise<void> {
  if (!supabase) return;
  
  try {
    const { error } = await supabase
      .from('user_accessibility_settings')
      .upsert({
        user_id: userId,
        high_contrast: settings.high_contrast,
        reduced_motion: settings.reduced_motion,
        font_size: settings.font_size,
        voice_navigation: settings.voice_navigation,
        keyboard_shortcuts: settings.keyboard_shortcuts,
        dyslexia_font: settings.dyslexia_font,
        plain_language: settings.plain_language,
        updated_at: new Date().toISOString(),
      });
    
    if (error) {
      throw error;
    }
  } catch (error) {
    console.warn('Error saving to database:', error);
    throw error;
  }
}

/**
 * Load settings from localStorage
 */
function loadFromLocalStorage(): AccessibilitySettings | null {
  try {
    const stored = localStorage.getItem(STORAGE_KEY);
    if (stored) {
      const parsed = JSON.parse(stored);
      // Merge with defaults to handle missing properties from older versions
      return { ...defaultSettings, ...parsed };
    }
  } catch (error) {
    console.warn('Error loading from localStorage:', error);
  }
  return null;
}

/**
 * Save settings to localStorage
 */
function saveToLocalStorage(settings: AccessibilitySettings): void {
  try {
    localStorage.setItem(STORAGE_KEY, JSON.stringify(settings));
  } catch (error) {
    console.warn('Error saving to localStorage:', error);
  }
}

/**
 * Clear all accessibility settings (useful for reset/logout)
 */
export async function clearAccessibilitySettings(): Promise<void> {
  try {
    // Clear localStorage
    localStorage.removeItem(STORAGE_KEY);
    
    // Clear database if user is authenticated
    if (isSupabaseAvailable()) {
      const user = await getCurrentUser();
      if (user) {
        await supabase
          ?.from('user_accessibility_settings')
          .delete()
          .eq('user_id', user.id);
      }
    }
  } catch (error) {
    console.warn('Error clearing accessibility settings:', error);
  }
}

/**
 * Sync settings from database (useful after login)
 */
export async function syncFromDatabase(): Promise<AccessibilitySettings | null> {
  if (!isSupabaseAvailable()) return null;
  
  try {
    const user = await getCurrentUser();
    if (!user) return null;
    
    const dbSettings = await loadFromDatabase(user.id);
    if (dbSettings) {
      saveToLocalStorage(dbSettings);
      return dbSettings;
    }
  } catch (error) {
    console.warn('Error syncing from database:', error);
  }
  
  return null;
}

