      console.warn('Supabase not configured - cannot fetch flows');
      return [];
    }
    
    const { data, error } = await supabase
      .from('flows')
      .select('*')
      .contains('age_tiers', [ageTier])
      .overlaps('tags', tags);
    
    if (error) {
      console.error('Error fetching flows by tags:', error);
      return [];
    }
    
    return data || [];
  },

  /**
   * Fetch all flows for an age tier
   */
  async getAllFlows(ageTier: string = 'adult'): Promise<FlowRecord[]> {
    if (!supabase) {
      console.warn('Supabase not configured - cannot fetch flows');
      return [];
    }
    
    const { data, error } = await supabase
      .from('flows')
      .select('*')
      .contains('age_tiers', [ageTier])
      .order('created_at', { ascending: false });
    
    if (error) {
      console.error('Error fetching all flows:', error);
      return [];
    }
    
    return data || [];
  }
};

