  created_at: string;
}

export interface CodexFeedbackRecord {
  id: string;
  user_id: string | null;
  session_id: string;
  section_id: string;
  rating: number; // 1-5 scale
  feedback_text?: string;
  experience_type: 'learning' | 'quiz' | 'overall';
  created_at: string;
}

/**
 * Codex analytics operations
 */
export const codexOperations = {
  /**
   * Create or update user session
   */
  async trackUserSession(sessionId: string, userId?: string): Promise<void> {
    if (!supabase) {
      console.warn('Supabase not configured - user session will not be tracked');
      return;
    }

    const { data: existing, error: fetchError } = await supabase
      .from('codex_users')
      .select('*')
      .eq('session_id', sessionId)
      .single();

    if (fetchError && fetchError.code !== 'PGRST116') {
      console.error('Error fetching user session:', fetchError);
      return;
    }

    if (existing) {
      // Update existing session
      const { error } = await supabase
        .from('codex_users')
        .update({
          last_activity: new Date().toISOString(),
          total_sessions: existing.total_sessions + 1,
          updated_at: new Date().toISOString()
        })
        .eq('id', existing.id);

      if (error) {
        console.error('Error updating user session:', error);
      }
    } else {
      // Create new user record
      const { error } = await supabase
        .from('codex_users')
        .insert({
          user_id: userId || null,
          session_id: sessionId,
          first_seen: new Date().toISOString(),
          last_activity: new Date().toISOString(),
          total_sessions: 1
        });

      if (error) {
        console.error('Error creating user session:', error);
      }
    }
  },

  /**
   * Track section completion
   */
  async trackSectionCompletion(
    sessionId: string,
    sectionId: string,
    subsectionIndex: number | null,
    completionTime: number,
    userId?: string
  ): Promise<void> {
    if (!supabase) {
      console.warn('Supabase not configured - section completion will not be tracked');
      return;
    }

    const { error } = await supabase
      .from('codex_section_completions')
      .insert({
        user_id: userId || null,
        session_id: sessionId,
        section_id: sectionId,
        subsection_index: subsectionIndex,
        completion_time: completionTime,
        completed_at: new Date().toISOString()
      });

    if (error) {
      console.error('Error tracking section completion:', error);
    }
  },

  /**
   * Track quiz completion
   */
  async trackQuizCompletion(
    sessionId: string,
    sectionId: string,
    quizScore: number,
    totalQuestions: number,
    timeTaken: number,
    attempts: number,
    userId?: string
  ): Promise<void> {
    if (!supabase) {
      console.warn('Supabase not configured - quiz completion will not be tracked');
      return;
    }

    const { error } = await supabase
      .from('codex_quiz_completions')
      .insert({
        user_id: userId || null,
        session_id: sessionId,
        section_id: sectionId,
        quiz_score: quizScore,
        total_questions: totalQuestions,
        time_taken: timeTaken,
        attempts: attempts,
        completed_at: new Date().toISOString()
      });

    if (error) {
      console.error('Error tracking quiz completion:', error);
    }
  },

  /**
   * Submit user feedback
   */
  async submitFeedback(
    sessionId: string,
    sectionId: string,
    rating: number,
    experienceType: 'learning' | 'quiz' | 'overall',
    feedbackText?: string,
    userId?: string
  ): Promise<void> {
    if (!supabase) {
      console.warn('Supabase not configured - feedback will not be submitted');
      return;
    }

    const { error } = await supabase
      .from('codex_feedback')
      .insert({
        user_id: userId || null,
        session_id: sessionId,
        section_id: sectionId,
        rating: rating,
        feedback_text: feedbackText || null,
        experience_type: experienceType
      });

    if (error) {
      console.error('Error submitting feedback:', error);
    }
  },

  /**
   * Get completion statistics for a section
   */
  async getSectionStats(sectionId: string): Promise<{
    totalCompletions: number;
    avgCompletionTime: number;
    quizCompletions: number;
    avgQuizScore: number;
    avgRating: number;
  } | null> {
    if (!supabase) {
      console.warn('Supabase not configured - cannot fetch section stats');
      return null;
    }

    // Get section completions
    const { data: completions, error: completionsError } = await supabase
      .from('codex_section_completions')
      .select('completion_time')
      .eq('section_id', sectionId);

    if (completionsError) {
      console.error('Error fetching section completions:', completionsError);
      return null;
    }

    // Get quiz completions
    const { data: quizzes, error: quizzesError } = await supabase
      .from('codex_quiz_completions')
      .select('quiz_score, total_questions')
      .eq('section_id', sectionId);

    if (quizzesError) {
      console.error('Error fetching quiz completions:', quizzesError);
      return null;
    }

    // Get feedback ratings
    const { data: feedback, error: feedbackError } = await supabase
      .from('codex_feedback')
      .select('rating')
      .eq('section_id', sectionId);

    if (feedbackError) {
      console.error('Error fetching feedback:', feedbackError);
      return null;
    }

    // Calculate statistics
    const totalCompletions = completions?.length || 0;
    const avgCompletionTime = totalCompletions > 0 
      ? completions!.reduce((sum, c) => sum + c.completion_time, 0) / totalCompletions 
      : 0;
    
    const quizCompletions = quizzes?.length || 0;
    const avgQuizScore = quizCompletions > 0
      ? quizzes!.reduce((sum, q) => sum + (q.quiz_score / q.total_questions * 100), 0) / quizCompletions
      : 0;

    const ratingCount = feedback?.length || 0;
    const avgRating = ratingCount > 0
      ? feedback!.reduce((sum, f) => sum + f.rating, 0) / ratingCount
      : 0;

    return {
      totalCompletions,
      avgCompletionTime,
      quizCompletions,
      avgQuizScore,
      avgRating
    };
  }
};

