import React from 'react';
import { render, screen, fireEvent, waitFor } from '@testing-library/react';
import { BrowserRouter } from 'react-router-dom';
import { renderHook, act } from '@testing-library/react';

// Components and hooks
import CrisisSafetyBar from '../components/CrisisSafetyBar';
import ErrorBoundary from '../components/ErrorBoundary';
import AccessibilityBar from '../components/AccessibilityBar';
import { useCrisisMonitoring } from '../hooks/useCrisisMonitoring';
import { usePerformanceMonitoring } from '../hooks/usePerformanceMonitoring';

// Mock accessibility context
const mockAccessibilityContext = {
  isDyslexicFont: false,
  setIsDyslexicFont: jest.fn(),
  isHighContrast: false,
  setIsHighContrast: jest.fn(),
  fontSize: 16,
  setFontSize: jest.fn(),
  isReducedMotion: false,
  setIsReducedMotion: jest.fn(),
};

jest.mock('../context/AccessibilityContext', () => ({
  useAccessibility: () => mockAccessibilityContext,
}));

// Integration test wrapper component
const MentalHealthPlatformIntegration: React.FC<{ 
  triggerCrisis?: boolean;
  triggerError?: boolean;
}> = ({ triggerCrisis = false, triggerError = false }) => {
  const crisisMonitoring = useCrisisMonitoring();
  const performanceMonitoring = usePerformanceMonitoring('IntegrationTest');

  React.useEffect(() => {
    if (triggerCrisis) {
      crisisMonitoring.recordMoodInput(1, 'I feel hopeless and want to hurt myself');
    }
  }, [triggerCrisis, crisisMonitoring]);

  if (triggerError) {
    throw new Error('Integration test error');
  }

  return (
    <ErrorBoundary>
      <BrowserRouter>
        <div className="mental-health-platform">
          <AccessibilityBar />
          <main>
            <h1>Mental Health Platform Integration Test</h1>
            <p>Testing full system integration</p>
          </main>
          <CrisisSafetyBar 
            isVisible={crisisMonitoring.safetyCheckRequired} 
            riskLevel={crisisMonitoring.riskLevel}
          />
        </div>
      </BrowserRouter>
    </ErrorBoundary>
  );
};

describe('Mental Health Platform Integration Tests', () => {
  beforeEach(() => {
    jest.clearAllMocks();
    jest.spyOn(console, 'error').mockImplementation(() => {});
    jest.spyOn(console, 'warn').mockImplementation(() => {});
  });

  afterEach(() => {
    jest.restoreAllMocks();
  });

  test('full platform renders without accessibility violations', async () => {
    render(<MentalHealthPlatformIntegration />);
    
    // Check that all major components are present
    expect(screen.getByText('Mental Health Platform Integration Test')).toBeInTheDocument();
    
    // Accessibility features should be available
    expect(screen.getByLabelText(/high contrast/i)).toBeInTheDocument();
    expect(screen.getByLabelText(/dyslexia font/i)).toBeInTheDocument();
  });

  test('crisis detection triggers safety systems', async () => {
