    expect(screen.getByLabelText('Confirm you are feeling safe')).toBeInTheDocument();
    expect(screen.getByLabelText('Get immediate help')).toBeInTheDocument();
  });

  test('error boundary protects against crashes', () => {
    render(<MentalHealthPlatformIntegration triggerError={true} />);
    
    // Error boundary should catch the error
    expect(screen.getByText('Something went wrong')).toBeInTheDocument();
    expect(screen.getByText('Immediate Help Available')).toBeInTheDocument();
    
    // Crisis resources should always be available during errors
    expect(screen.getByLabelText('Call 988 Suicide & Crisis Lifeline')).toBeInTheDocument();
    expect(screen.getByLabelText('Call 911 for emergencies')).toBeInTheDocument();
  });

  test('accessibility controls work with crisis systems', async () => {
    render(<MentalHealthPlatformIntegration triggerCrisis={true} />);
    
    // Both accessibility and crisis systems should be active
    await waitFor(() => {
      expect(screen.getByTestId('crisis-safety-bar')).toBeInTheDocument();
    });
    
    expect(screen.getByLabelText(/high contrast/i)).toBeInTheDocument();
    
    // Test accessibility control interaction
