    fireEvent.click(highContrastToggle);
    
    expect(mockAccessibilityContext.setIsHighContrast).toHaveBeenCalled();
  });

  test('performance monitoring works during crisis scenarios', async () => {
    const { result } = renderHook(() => usePerformanceMonitoring('CrisisTest'));
    
    render(<MentalHealthPlatformIntegration triggerCrisis={true} />);
    
    // Performance monitoring should track the crisis scenario
    act(() => {
      const endInteraction = result.current.trackInteraction('crisis-detection');
      endInteraction();
    });
    
    const summary = result.current.getPerformanceSummary();
    expect(summary).toBeTruthy();
  });

  test('system handles multiple concurrent safety features', async () => {
    render(<MentalHealthPlatformIntegration triggerCrisis={true} />);
    
    // Crisis safety should be active
    await waitFor(() => {
      expect(screen.getByTestId('crisis-safety-bar')).toBeInTheDocument();
    });
    
    // Accessibility should remain functional
    expect(screen.getByLabelText(/dyslexia font/i)).toBeInTheDocument();
    
    // User can interact with safety confirmation
    const safeButton = screen.getByLabelText('Confirm you are feeling safe');
    fireEvent.click(safeButton);
    
    expect(screen.getByText(/Safety confirmed/)).toBeInTheDocument();
  });

  test('emergency escalation procedures are accessible', async () => {
    render(<MentalHealthPlatformIntegration triggerCrisis={true} />);
    
    await waitFor(() => {
      expect(screen.getByTestId('crisis-safety-bar')).toBeInTheDocument();
    });
    
    // Get help should show resources
    const helpButton = screen.getByLabelText('Get immediate help');
    fireEvent.click(helpButton);
    
    // Emergency resources should be available
    expect(screen.getByText('988 Suicide & Crisis Lifeline')).toBeInTheDocument();
    expect(screen.getByText('Crisis Text Line')).toBeInTheDocument();
    expect(screen.getByText('Emergency Services')).toBeInTheDocument();
    
    // Phone links should be functional
    const phoneLink = screen.getByLabelText('Call 988 Suicide & Crisis Lifeline at 988');
    expect(phoneLink).toHaveAttribute('href', 'tel:988');
  });

  test('system maintains stability under stress', async () => {
    // Simulate rapid user interactions
    render(<MentalHealthPlatformIntegration />);
    
    const highContrastToggle = screen.getByLabelText(/high contrast/i);
    const dyslexiaToggle = screen.getByLabelText(/dyslexia font/i);
    
    // Rapid toggling shouldn't crash the system
    for (let i = 0; i < 10; i++) {
