/**
 * module-isolation.test.tsx - Tests to verify strict module boundaries
 * Ensures that navigation only includes root-level module links
 */

import React from 'react';
import { render, screen } from '@testing-library/react';
import { BrowserRouter } from 'react-router-dom';
import MythologIQLayout from '../pages/MythologIQLayout';

// Mock Outlet for testing
jest.mock('react-router-dom', () => ({
  ...jest.requireActual('react-router-dom'),
  Outlet: () => <div data-testid="outlet">Content</div>,
  useLocation: () => ({ pathname: '/' })
}));

describe('Module Isolation Tests', () => {
  beforeEach(() => {
    // Clear any console warnings
    jest.spyOn(console, 'warn').mockImplementation(() => {});
    jest.spyOn(console, 'error').mockImplementation(() => {});
  });

  afterEach(() => {
    jest.restoreAllMocks();
  });

  test('navigation only includes root-level module links', () => {
    render(
      <BrowserRouter>
        <MythologIQLayout />
      </BrowserRouter>
    );

    // Check that only root module links are present
    const allowedModuleLinks = [
      '/insight',
      '/presence', 
      '/resonance'
    ];

    // Check navigation contains only allowed module links
    const navElement = screen.getByRole('banner'); // Header has role="banner"
    const links = navElement.querySelectorAll('a');
    
    const moduleLinks = Array.from(links)
      .map(link => link.getAttribute('href'))
      .filter(href => href && (
        href.includes('/insight') || 
        href.includes('/presence') || 
        href.includes('/resonance')
      ));

    // Verify only root-level module paths are present
    moduleLinks.forEach(link => {
      const isRootLevel = allowedModuleLinks.some(allowed => 
        link === allowed || link === `${allowed}/`
      );
      expect(isRootLevel).toBe(true);
    });
  });

  test('no deep module paths in navigation', () => {
    render(
      <BrowserRouter>
        <MythologIQLayout />
      </BrowserRouter>
    );

    const navElement = screen.getByRole('banner');
    const links = navElement.querySelectorAll('a');
    
    // Check for forbidden deep paths
    const forbiddenPatterns = [
      '/insight/tools/specific-tool',
      '/presence/agents/specific-agent',
      '/resonance/books/specific-book',
      '/modules/', // Direct module references
      '/insight/internal',
      '/presence/internal', 
      '/resonance/internal'
    ];

    Array.from(links).forEach(link => {
      const href = link.getAttribute('href') || '';
      forbiddenPatterns.forEach(pattern => {
        expect(href).not.toContain(pattern);
      });
    });
  });

  test('modules maintain visual separation', () => {
    render(
      <BrowserRouter>
        <MythologIQLayout />
      </BrowserRouter>
    );

    // Check for module-specific styling classes
    const moduleElements = screen.getByRole('banner').querySelectorAll('[class*="insight"], [class*="presence"], [class*="resonance"]');
    
    // Each module-specific element should only contain its own module classes
    Array.from(moduleElements).forEach(element => {
      const className = element.className;
      
      // Count module prefixes in class names
      const moduleCount = [
        (className.match(/insight-/g) || []).length > 0 ? 1 : 0,
        (className.match(/presence-/g) || []).length > 0 ? 1 : 0,
        (className.match(/resonance-/g) || []).length > 0 ? 1 : 0
      ].reduce((sum, count) => sum + count, 0);
      
      // Element should not mix module-specific classes
      expect(moduleCount).toBeLessThanOrEqual(1);
    });
  });

  test('accessibility bar maintains module boundaries', () => {
    render(
      <BrowserRouter>
        <MythologIQLayout />
      </BrowserRouter>
    );

    // AccessibilityBar should not contain module-specific references
    const accessibilityElements = document.querySelectorAll('[class*="accessibility"]');
    
    Array.from(accessibilityElements).forEach(element => {
      const textContent = element.textContent?.toLowerCase() || '';
      const className = element.className.toLowerCase();
      
      // Should not contain module-specific terms
      const forbiddenTerms = ['insight-tool', 'presence-agent', 'resonance-book'];
      forbiddenTerms.forEach(term => {
        expect(textContent).not.toContain(term);
        expect(className).not.toContain(term);
      });
    });
  });

  test('global navigation uses platform tokens only', () => {
    render(
      <BrowserRouter>
        <MythologIQLayout />
      </BrowserRouter>
    );

    // Check computed styles use CSS custom properties from platform tokens
    const headerElement = screen.getByRole('banner');
    const computedStyle = window.getComputedStyle(headerElement);
    
    // Should use global tokens, not module-specific ones
    const cssText = computedStyle.cssText || '';
    
    // Should not contain module-specific CSS variables
    expect(cssText).not.toMatch(/--insight-/);
    expect(cssText).not.toMatch(/--presence-/);
    expect(cssText).not.toMatch(/--resonance-/);
  });
});

describe('Module Theme Isolation Tests', () => {
  test('CSS custom properties follow naming conventions', () => {
    // This test ensures our CSS variables follow the established patterns
    const globalTokenPattern = /^--(color|font|spacing|radius|shadow|transition|duration|z-index|breakpoint|button|input|focus|min|aa|aaa)-/;
    const modulePatterns = {
      presence: /^--presence-/,
      insight: /^--insight-/,
      resonance: /^--resonance-/
    };

    // Test would normally check actual CSS files, but for now we verify patterns
    const testVariables = [
      '--color-accent', // âœ“ Global token
      '--font-family-base', // âœ“ Global token  
      '--presence-accent', // âœ“ Presence module
      '--insight-tool-bg', // âœ“ Insight module
      '--resonance-book-border', // âœ“ Resonance module
    ];

    testVariables.forEach(variable => {
      const isGlobalToken = globalTokenPattern.test(variable);
      const isModuleToken = Object.values(modulePatterns).some(pattern => pattern.test(variable));
      
      expect(isGlobalToken || isModuleToken).toBe(true);
    });
  });

  test('module CSS classes follow naming conventions', () => {
    const validPatterns = [
      /^[a-z][a-z0-9]*(-[a-z0-9]+)*$/, // Generic kebab-case
      /^presence-[a-z0-9-]+$/, // Presence module
      /^insight-[a-z0-9-]+$/, // Insight module  
      /^resonance-[a-z0-9-]+$/ // Resonance module
    ];

    const testClasses = [
      'nav-item', // âœ“ Generic
      'presence-agent-card', // âœ“ Presence module
      'insight-tool-wrapper', // âœ“ Insight module
      'resonance-book-shelf', // âœ“ Resonance module
    ];

    testClasses.forEach(className => {
      const isValid = validPatterns.some(pattern => pattern.test(className));
      expect(isValid).toBe(true);
    });
  });
});

describe('Import Boundary Tests', () => {
  test('prevents cross-module imports at compile time', () => {
    // This test documents the expected behavior - actual enforcement is via ESLint
    const forbiddenImports = [
      "import { PresenceAgent } from '../presence/components/Agent'", // âŒ Cross-module
      "import InsightTool from '../../insight/tools/Tool'", // âŒ Cross-module
      "import { ResonanceBook } from '../resonance/types/Book'" // âŒ Cross-module
    ];

    const allowedImports = [
      "import { PlatformUtil } from '../platform/utils'", // âœ“ Platform import
      "import tokens from '../platform/theme/tokens.css'", // âœ“ Global tokens
      "import SharedComponent from '../components/shared/Component'" // âœ“ Shared component
    ];

    // Document expected behavior (actual enforcement via ESLint rules)
    expect(forbiddenImports).toHaveLength(3);
    expect(allowedImports).toHaveLength(3);
  });
});

