use std::fs;
use std::path::{Path, PathBuf};
use std::sync::Arc;

use serde::{Deserialize, Serialize};
use serde_json::{json, Value};
use tauri::Manager;
use tauri::{AppHandle, State};

use crate::auth::SecureAuthenticator;
use crate::guards::AuthenticatedRequest;
use crate::require_auth;
use crate::require_ownership;
use crate::secure_vault::SecureVault;

const MAX_STORE_BYTES: usize = 2 * 1024 * 1024;

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct GetAvatarFoundationStoreRequest {
    #[serde(alias = "userId")]
    pub user_id: String,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct SaveAvatarFoundationStoreRequest {
    #[serde(alias = "userId")]
    pub user_id: String,
    pub store: Value,
}

fn sanitize_user_id(user_id: &str) -> Result<String, String> {
    if user_id.is_empty() {
        return Err("user_id cannot be empty".to_string());
    }
    if user_id.len() > 128 {
        return Err("user_id exceeds max length 128".to_string());
    }
    if !user_id
        .chars()
        .all(|c| c.is_ascii_alphanumeric() || c == '-' || c == '_')
    {
        return Err("user_id contains invalid characters".to_string());
    }
    Ok(user_id.to_string())
}

fn foundation_store_path(app_handle: &AppHandle, user_id: &str) -> Result<PathBuf, String> {
    let base = app_handle
        .path()
        .app_data_dir()
        .map_err(|e| format!("Failed to resolve app_data_dir: {e}"))?;
    Ok(base
        .join("synapse")
        .join("plugins")
        .join("bust-avatar")
        .join("stores")
        .join(format!("{user_id}.json")))
}

fn ensure_store_parent(path: &Path) -> Result<(), String> {
    let parent = path.parent().ok_or("Invalid store path")?;
    fs::create_dir_all(parent).map_err(|e| format!("Failed to create store directory: {e}"))?;
    Ok(())
}

fn default_store() -> Value {
    json!({
        "specs": {},
        "personaAssignments": {},
        "updatedAt": chrono::Utc::now().to_rfc3339(),
        "schemaVersion": 1
    })
}

fn normalize_store_json(raw: &Value) -> Value {
    let specs = raw
        .get("specs")
        .and_then(|v| v.as_object())
        .map(|m| Value::Object(m.clone()))
        .unwrap_or_else(|| json!({}));

    let persona_assignments = raw
        .get("personaAssignments")
        .and_then(|v| v.as_object())
        .map(|m| Value::Object(m.clone()))
        .unwrap_or_else(|| json!({}));

    let updated_at = raw
        .get("updatedAt")
        .and_then(|v| v.as_str())
        .map(|s| s.to_string())
        .unwrap_or_else(|| chrono::Utc::now().to_rfc3339());

    json!({
        "specs": specs,
        "personaAssignments": persona_assignments,
        "updatedAt": updated_at,
        "schemaVersion": 1
    })
}

#[tauri::command]
pub async fn operator_avatar_foundation_get(
    auth: State<'_, Arc<SecureAuthenticator>>,
    vault: State<'_, SecureVault>,
    request: AuthenticatedRequest<GetAvatarFoundationStoreRequest>,
    app_handle: AppHandle,
) -> Result<Value, String> {
    let authenticated_user = require_auth!(auth, &request.auth_token);
    require_ownership!(authenticated_user, &request.payload.user_id);
    let user_id = sanitize_user_id(&request.payload.user_id)?;

    let store_path = foundation_store_path(&app_handle, &user_id)?;
    if !store_path.exists() {
        let metadata = json!({
            "user_id": user_id,
            "action": "foundation_get_default"
        });
        if let Err(e) = vault
            .log_audit_event(
                &authenticated_user,
                "avatar_foundation_read",
                Some("operator_avatar_foundation"),
                "success",
                None,
                Some(metadata),
            )
            .await
        {
            eprintln!("[AvatarFoundation] Audit log failed: {e}");
        }
        return Ok(default_store());
    }

    let content =
        fs::read_to_string(&store_path).map_err(|e| format!("Failed to read store file: {e}"))?;
    let parsed =
        serde_json::from_str::<Value>(&content).map_err(|e| format!("Invalid store JSON: {e}"))?;
    let normalized = normalize_store_json(&parsed);

    if normalized != parsed {
        let migrated = serde_json::to_string_pretty(&normalized)
            .map_err(|e| format!("Failed to serialize migrated store JSON: {e}"))?;
        fs::write(&store_path, migrated)
            .map_err(|e| format!("Failed to write migrated store JSON: {e}"))?;
    }

    let metadata = json!({
        "user_id": user_id,
        "action": "foundation_get"
    });
    if let Err(e) = vault
        .log_audit_event(
            &authenticated_user,
            "avatar_foundation_read",
            Some("operator_avatar_foundation"),
            "success",
            None,
            Some(metadata),
        )
        .await
    {
        eprintln!("[AvatarFoundation] Audit log failed: {e}");
    }

    Ok(normalized)
}

#[tauri::command]
pub async fn operator_avatar_foundation_save(
    auth: State<'_, Arc<SecureAuthenticator>>,
    vault: State<'_, SecureVault>,
    request: AuthenticatedRequest<SaveAvatarFoundationStoreRequest>,
    app_handle: AppHandle,
) -> Result<(), String> {
    let authenticated_user = require_auth!(auth, &request.auth_token);
    require_ownership!(authenticated_user, &request.payload.user_id);
    let user_id = sanitize_user_id(&request.payload.user_id)?;

    if !request.payload.store.is_object() {
        return Err("store must be a JSON object".to_string());
    }

    let normalized = normalize_store_json(&request.payload.store);
    let store_path = foundation_store_path(&app_handle, &user_id)?;
    ensure_store_parent(&store_path)?;

    let serialized = serde_json::to_string_pretty(&normalized)
        .map_err(|e| format!("Failed to serialize store JSON: {e}"))?;
    if serialized.len() > MAX_STORE_BYTES {
        return Err(format!(
            "store exceeds max size of {} bytes",
            MAX_STORE_BYTES
        ));
    }
    fs::write(&store_path, serialized).map_err(|e| format!("Failed to write store file: {e}"))?;

    let metadata = json!({
        "user_id": user_id,
        "action": "foundation_save"
    });
    if let Err(e) = vault
        .log_audit_event(
            &authenticated_user,
            "avatar_foundation_write",
            Some("operator_avatar_foundation"),
            "success",
            None,
            Some(metadata),
        )
        .await
    {
        eprintln!("[AvatarFoundation] Audit log failed: {e}");
    }

    Ok(())
}

