//! Operator Adaptive Learning Engine
//!
//! Per-persona style learning with pattern analysis and user approval gates.

use chrono::Utc;
use serde::{Deserialize, Serialize};
use std::collections::HashMap;
use uuid::Uuid;

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct StyleObservation {
    pub persona_id: String,
    pub message_content: String,
    pub word_count: usize,
    pub uses_emoji: bool,
    pub formality_score: f32, // 0.0 = casual, 1.0 = formal
    pub observed_at: String,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct PendingAdaptation {
    pub id: String,
    pub persona_id: String,
    pub adaptation_type: AdaptationType,
    pub current_value: String,
    pub proposed_value: String,
    pub confidence: f32,
    pub evidence_count: usize,
    pub created_at: String,
    pub status: AdaptationStatus,
}

#[derive(Debug, Clone, Serialize, Deserialize, PartialEq, Default)]
pub enum AdaptationType {
    #[default]
    ResponseLength,
    FormalityLevel,
    EmojiUsage,
    VocabularyStyle,
    AvatarExpression, // Multi-dimensional: avatars
    VoiceTone,        // Multi-dimensional: voice
}

#[derive(Debug, Clone, Serialize, Deserialize, PartialEq)]
pub enum AdaptationStatus {
    Pending,
    Approved,
    Rejected,
}

pub struct LearningEngine {
    observations: HashMap<String, Vec<StyleObservation>>,
    pending: Vec<PendingAdaptation>,
    min_observations: usize,
    confidence_threshold: f32,
}

impl Default for LearningEngine {
    fn default() -> Self {
        Self::new()
    }
}

impl LearningEngine {
    pub fn new() -> Self {
        Self {
            observations: HashMap::new(),
            pending: Vec::new(),
            min_observations: 20,
            confidence_threshold: 0.75,
        }
    }

    pub fn observe(&mut self, obs: StyleObservation) {
        let persona_id = obs.persona_id.clone();
        let persona_obs = self.observations.entry(persona_id.clone()).or_default();
        persona_obs.push(obs);
        self.analyze_and_propose(&persona_id);
    }

    fn analyze_and_propose(&mut self, persona_id: &str) {
        // Extract all needed values before releasing the borrow
        let (length_category, formality_category, emoji_rate, obs_len, confidence) = {
            let obs = match self.observations.get(persona_id) {
                Some(o) if o.len() >= self.min_observations => o,
                _ => return,
            };

            // Analyze response length trend
            let avg_words: f32 =
                obs.iter().map(|o| o.word_count as f32).sum::<f32>() / obs.len() as f32;
            let length_cat = if avg_words < 50.0 {
                "concise"
            } else if avg_words < 150.0 {
                "moderate"
            } else {
                "verbose"
            };

            // Analyze formality trend
            let avg_formality: f32 =
                obs.iter().map(|o| o.formality_score).sum::<f32>() / obs.len() as f32;
            let formality_cat = if avg_formality < 0.3 {
                "casual"
            } else if avg_formality < 0.7 {
                "balanced"
            } else {
                "formal"
            };

            // Analyze emoji usage
            let emoji_r: f32 =
                obs.iter().filter(|o| o.uses_emoji).count() as f32 / obs.len() as f32;

            let conf = self.calculate_confidence(obs);
            (length_cat, formality_cat, emoji_r, obs.len(), conf)
        };

        // Propose adaptations if confident (borrow is now released)
        if confidence >= self.confidence_threshold {
            self.propose_adaptation(
                persona_id,
                AdaptationType::ResponseLength,
                "default",
                length_category,
                obs_len,
            );
            self.propose_adaptation(
                persona_id,
                AdaptationType::FormalityLevel,
                "balanced",
                formality_category,
                obs_len,
            );
            if emoji_rate > 0.5 {
                self.propose_adaptation(
                    persona_id,
                    AdaptationType::EmojiUsage,
                    "minimal",
                    "frequent",
                    obs_len,
                );
            }
        }
    }

    fn calculate_confidence(&self, obs: &[StyleObservation]) -> f32 {
        let n = obs.len() as f32;
        (n / (n + 10.0)).min(0.95) // Asymptotic confidence
    }

    fn propose_adaptation(
        &mut self,
        persona_id: &str,
        atype: AdaptationType,
        current: &str,
        proposed: &str,
        evidence: usize,
    ) {
        // Skip if already pending for this persona+type
        if self.pending.iter().any(|p| {
            p.persona_id == persona_id
                && p.adaptation_type == atype
                && p.status == AdaptationStatus::Pending
        }) {
            return;
        }
        self.pending.push(PendingAdaptation {
            id: Uuid::new_v4().to_string(),
            persona_id: persona_id.to_string(),
            adaptation_type: atype,
            current_value: current.to_string(),
            proposed_value: proposed.to_string(),
            confidence: self.confidence_threshold,
            evidence_count: evidence,
            created_at: Utc::now().to_rfc3339(),
            status: AdaptationStatus::Pending,
        });
    }

    pub fn get_pending(&self, persona_id: &str) -> Vec<PendingAdaptation> {
        self.pending
            .iter()
            .filter(|p| p.persona_id == persona_id && p.status == AdaptationStatus::Pending)
            .cloned()
            .collect()
    }

    pub fn approve(&mut self, id: &str) -> Result<(), String> {
        let adaptation = self
            .pending
            .iter_mut()
            .find(|p| p.id == id)
            .ok_or("Adaptation not found")?;
        adaptation.status = AdaptationStatus::Approved;
        Ok(())
    }

    pub fn reject(&mut self, id: &str) -> Result<(), String> {
        let adaptation = self
            .pending
            .iter_mut()
            .find(|p| p.id == id)
            .ok_or("Adaptation not found")?;
        adaptation.status = AdaptationStatus::Rejected;
        Ok(())
    }
}

