//! Operator Learning Persistence - Database operations for observations and adaptation history.

use super::learning::{AdaptationType, StyleObservation};
use crate::db_manager::DatabaseManager;
use chrono::Utc;
use serde::{Deserialize, Serialize};
use uuid::Uuid;

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct ObservationRecord {
    pub id: String,
    pub persona_id: String,
    pub observation: StyleObservation,
    pub context_type: String,
    pub session_id: String,
    pub created_at: String,
}

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct AdaptationHistory {
    pub id: String,
    pub persona_id: String,
    pub adaptation_type: AdaptationType,
    pub old_value: String,
    pub new_value: String,
    pub applied_at: String,
    pub rolled_back_at: Option<String>,
}

impl ObservationRecord {
    pub fn new(
        persona_id: &str,
        obs: StyleObservation,
        context_type: &str,
        session_id: &str,
    ) -> Self {
        Self {
            id: Uuid::new_v4().to_string(),
            persona_id: persona_id.to_string(),
            observation: obs,
            context_type: context_type.to_string(),
            session_id: session_id.to_string(),
            created_at: Utc::now().to_rfc3339(),
        }
    }
}

pub async fn persist_observation(
    db: &DatabaseManager,
    record: &ObservationRecord,
) -> Result<(), String> {
    let obs_json =
        serde_json::to_string(&record.observation).map_err(|e| format!("Serialize error: {e}"))?;

    sqlx::query(
        "INSERT INTO operator_observations (id, persona_id, observation, context_type, session_id, created_at)
         VALUES (?, ?, ?, ?, ?, ?)"
    )
    .bind(&record.id)
    .bind(&record.persona_id)
    .bind(&obs_json)
    .bind(&record.context_type)
    .bind(&record.session_id)
    .bind(&record.created_at)
    .execute(&db.write_pool)
    .await
    .map_err(|e| format!("DB error: {e}"))?;

    Ok(())
}

pub async fn load_observations(
    db: &DatabaseManager,
    persona_id: &str,
    limit: usize,
) -> Result<Vec<ObservationRecord>, String> {
    let rows: Vec<(String, String, String, String, String, String)> = sqlx::query_as(
        "SELECT id, persona_id, observation, context_type, session_id, created_at
         FROM operator_observations WHERE persona_id = ? ORDER BY created_at DESC LIMIT ?",
    )
    .bind(persona_id)
    .bind(limit as i64)
    .fetch_all(&db.read_pool)
    .await
    .unwrap_or_default();

    rows.into_iter()
        .map(|(id, pid, obs_json, ctx, sess, created)| {
            let observation: StyleObservation =
                serde_json::from_str(&obs_json).map_err(|e| format!("Parse error: {e}"))?;
            Ok(ObservationRecord {
                id,
                persona_id: pid,
                observation,
                context_type: ctx,
                session_id: sess,
                created_at: created,
            })
        })
        .collect()
}

pub fn calculate_context_score(observations: &[ObservationRecord], context_type: &str) -> f32 {
    if observations.is_empty() {
        return 0.0;
    }
    let matching = observations
        .iter()
        .filter(|o| o.context_type == context_type)
        .count();
    (matching as f32 / observations.len() as f32).clamp(0.0, 1.0)
}

