//! Operator Adaptation Guardrails - Alice-informed behavioral safety gates.

use super::learning::{AdaptationType, PendingAdaptation};
use crate::alice::signals::ComputedSignal;
use serde::{Deserialize, Serialize};

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct AdaptationGuardrail {
    pub max_change_rate: f32,
    pub stress_threshold: f64,
    pub cognitive_load_threshold: f64,
    pub min_confidence_critical: f32,
}

#[derive(Debug, Clone, Serialize, Deserialize, PartialEq)]
pub enum GuardrailDecision {
    Allow,
    Defer { reason: String },
    Block { reason: String },
}

impl Default for AdaptationGuardrail {
    fn default() -> Self {
        Self {
            max_change_rate: 0.25,
            stress_threshold: 75.0,
            cognitive_load_threshold: 80.0,
            min_confidence_critical: 0.85,
        }
    }
}

impl AdaptationGuardrail {
    pub fn evaluate(
        &self,
        adaptation: &PendingAdaptation,
        signals: Option<&ComputedSignal>,
    ) -> GuardrailDecision {
        if let Some(sig) = signals {
            if sig.stress > self.stress_threshold {
                return GuardrailDecision::Defer {
                    reason: format!(
                        "User stress ({:.0}) exceeds threshold ({:.0})",
                        sig.stress, self.stress_threshold
                    ),
                };
            }

            if sig.cognitive_load > self.cognitive_load_threshold {
                return GuardrailDecision::Defer {
                    reason: format!(
                        "Cognitive load ({:.0}) exceeds threshold ({:.0})",
                        sig.cognitive_load, self.cognitive_load_threshold
                    ),
                };
            }
        }

        let is_critical = matches!(
            adaptation.adaptation_type,
            AdaptationType::VoiceTone | AdaptationType::AvatarExpression
        );

        if is_critical && adaptation.confidence < self.min_confidence_critical {
            return GuardrailDecision::Block {
                reason: format!(
                    "Critical adaptation requires {:.0}% confidence, got {:.0}%",
                    self.min_confidence_critical * 100.0,
                    adaptation.confidence * 100.0
                ),
            };
        }

        GuardrailDecision::Allow
    }
}

